r\nindex 0af70da..e72f54a 100644\r\n--- a/src/main/java/br/ime/usp/commendans/controller/IndexController.java\r\n+++ b/src/main/java/br/ime/usp/commendans/controller/IndexController.java\r\n@@ -24,9 +24,8 @@ public class IndexController {\r\n         this.recommenderCreator = recommenderCreator;\r\n     }\r\n     \r\n-    @Get(\"/helloworld\")\r\n+    @Get(\"/\")\r\n     public void index() {\r\n-        result.use(Results.http()).body(\"<html><body><h1>hello world</h1</body></html>\");\r\n     }\r\n     \r\n     @Get(\"/import/askjdhakjshdakjshd\")\r\ndiff --git a/src/main/webapp/WEB-INF/jsp/index/index.jsp b/src/main/webapp/WEB-INF/jsp/index/index.jsp\r\nnew file mode 100644\r\nindex 0000000..2e5705f\r\n--- /dev/null\r\n+++ b/src/main/webapp/WEB-INF/jsp/index/index.jsp\r\n@@ -0,0 +1,55 @@\r\n+<%@ page language=\"java\" contentType=\"text/html; charset=UTF-8\"\r\n+    pageEncoding=\"UTF-8\"%>\r\n+<%@ taglib uri=\"http://java.sun.com/jsp/jstl/core\" prefix=\"c\" %>\r\n+<!DOCTYPE html PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\" \"http://www.w3.org/TR/html4/loose.dtd\">\r\n+<html>\r\n+<head>\r\n+<meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\">\r\n+<title>Commendans</title>\r\n+</head>\r\n+<body>\r\n+\r\n+	<p>Welcome to commendans, an REST recommendation service.<p>\r\n+	\r\n+	<p>You may register your client app <a href=\'<c:url value=\"/app/new\" />\'>here</a></p>\r\n+	\r\n+	<p>Some routes implemented:</p>\r\n+	<ul>\r\n+		<li><pre>GET <c:url value=\"/recommend/item/\" />{appItemId}</pre>\r\n+			<ul>\r\n+				<li><strong>Returns a sorted array of recommendations given an item of your app (identified with id <code>appItemId</code>)</strong></li>\r\n+				<li>Parameters:</li>\r\n+				<ul>\r\n+					<li><pre>accessKey: {your-access-key}</pre></li>\r\n+				</ul>\r\n+			</ul>\r\n+		</li>\r\n+		<li><pre>GET <c:url value=\"/recommend/items/\" /></pre>\r\n+			<ul>\r\n+				<li><strong>Returns a sorted array of recommendations given an array of items of your app (identified with ids array <code>items</code>)</strong></li>\r\n+				<li>Parameters:</li>\r\n+				<ul>\r\n+					<li><pre>accessKey: {your-access-key}</pre></li>\r\n+					<li><pre>items[0]: {some-item-id}</pre></li>\r\n+					<li><pre>items[1]: {some-item-id}</pre></li>\r\n+					<li><pre>items[...]: ...</pre></li>\r\n+				</ul>\r\n+			</ul>\r\n+		</li>\r\n+		<li><pre>POST <c:url value=\"/app/addSale\" /></pre>\r\n+			<ul>\r\n+				<li><strong>Register a event of your app, given a user id and a list of items bought (or liked, or visited, whatever)</strong></li>\r\n+				<li>Parameters:</li>\r\n+				<ul>\r\n+					<li><pre>accessKey: {your-access-key}</pre></li>\r\n+					<li><pre>clientAppUserId: {some-user-id}</pre></li>\r\n+					<li><pre>items[0]: {some-item-id}</pre></li>\r\n+					<li><pre>items[1]: {some-item-id}</pre></li>\r\n+					<li><pre>items[...]: ...</pre></li>\r\n+				</ul>\r\n+			</ul>\r\n+		</li>\r\n+	</ul>\r\n+\r\n+</body>\r\n+</html>\r\n\\ No newline at end of file\r\n'),(57,'\r\ndiff --git a/src/main/java/br/ime/usp/commendans/controller/IndexController.java b/src/main/java/br/ime/usp/commendans/controller/IndexController.java\r\nindex e72f54a..c443794 100644\r\n--- a/src/main/java/br/ime/usp/commendans/controller/IndexController.java\r\n+++ b/src/main/java/br/ime/usp/commendans/controller/IndexController.java\r\n@@ -1,13 +1,11 @@\r\n package br.ime.usp.commendans.controller;\r\n \r\n-import org.apache.commons.codec.digest.DigestUtils;\r\n import org.hibernate.Session;\r\n \r\n import br.com.caelum.vraptor.Get;\r\n import br.com.caelum.vraptor.Resource;\r\n import br.com.caelum.vraptor.Result;\r\n import br.com.caelum.vraptor.view.Results;\r\n-import br.ime.usp.commendans.infra.DataImporter;\r\n import br.ime.usp.commendans.recommender.RecommenderCreator;\r\n \r\n \r\n@@ -30,12 +28,12 @@ public class IndexController {\r\n     \r\n     @Get(\"/import/askjdhakjshdakjshd\")\r\n     public void importData() {\r\n-        DataImporter dataImporter = n