nimport br.ime.usp.commendans.model.Item;\r\nimport br.ime.usp.commendans.model.User;\r\n\r\npublic class ItemToItemRecommenderFactoryTest {\r\n\r\n    @Test\r\n    public void test() {\r\n        ItemToItemRecommenderFactory factory = new ItemToItemRecommenderFactory();\r\n        List<Item> items = new ArrayList<Item>();\r\n        factory.addUser(new User(items));\r\n    }\r\n\r\n}\r\n',555,40),(41,'',0,41),(42,'package br.ime.usp.commendans;\r\n\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\nimport java.util.Map;\r\nimport java.util.Set;\r\n\r\nimport br.ime.usp.commendans.factory.ItemToItemsAssociation;\r\nimport br.ime.usp.commendans.factory.ItemToItemsAssociation.Tuple;\r\nimport br.ime.usp.commendans.model.Item;\r\n\r\npublic class ItemToItemRecommender implements Recommender {\r\n\r\n    private final Map<Item, List<Tuple>> associations;\r\n\r\n    public ItemToItemRecommender(\r\n            HashMap<Item, ItemToItemsAssociation> associations) {\r\n        this.associations = parse(associations);\r\n    }\r\n\r\n    private Map<Item, List<Tuple>> parse(HashMap<Item, ItemToItemsAssociation> associations) {\r\n        HashMap<Item, List<Tuple>> map = new HashMap<Item, List<Tuple>>();\r\n        Set<Item> items = associations.keySet();\r\n        for (Item item : items) {\r\n            ItemToItemsAssociation association = associations.get(item);\r\n            List<Tuple> tuples = association.toTupleList();\r\n            map.put(item, tuples);\r\n        }\r\n        return map;\r\n    }\r\n\r\n    @Override\r\n    public List<Tuple> recommendendItemsFor(Item item) {\r\n        List<Tuple> tuples = associations.get(item);\r\n        return tuples;\r\n    }\r\n\r\n}\r\n',1221,42),(43,'package br.ime.usp.commendans;\r\n\r\nimport java.util.List;\r\n\r\nimport br.ime.usp.commendans.factory.ItemToItemsAssociation.Tuple;\r\nimport br.ime.usp.commendans.model.Item;\r\n\r\npublic interface Recommender {\r\n    public List<Tuple> recommendendItemsFor(Item item);\r\n}\r\n',264,43),(44,'package br.ime.usp.commendans.factory;\r\n\r\nimport java.util.ArrayList;\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\nimport java.util.Map;\r\nimport java.util.Set;\r\n\r\nimport br.ime.usp.commendans.ItemToItemRecommender;\r\nimport br.ime.usp.commendans.model.Item;\r\nimport br.ime.usp.commendans.model.User;\r\n\r\npublic class ItemToItemRecommenderFactory {\r\n\r\n    private Map<Item, List<User>> usersByItemBought;\r\n    private HashMap<Item, ItemToItemsAssociation> associations;\r\n    \r\n    public ItemToItemRecommenderFactory() {\r\n        usersByItemBought = new HashMap<Item, List<User>>();\r\n    }\r\n    \r\n    public void addUser(User user) {\r\n        List<Item> items = user.itemsBought();\r\n        for (Item item : items) {\r\n            store(item, user);\r\n        }\r\n    }\r\n\r\n    private void store(Item item, User user) {\r\n        List<User> users = usersByItemBought.get(item);\r\n        if (users == null) {\r\n            users = new ArrayList<User>();\r\n        }\r\n        users.add(user);\r\n        usersByItemBought.put(item, users);\r\n    }\r\n    \r\n    \r\n    public ItemToItemRecommender build() {\r\n        associations = new HashMap<Item, ItemToItemsAssociation>();\r\n        Set<Item> items = usersByItemBought.keySet();\r\n        for (Item item : items) {\r\n            ItemToItemsAssociation association = new ItemToItemsAssociation(item);\r\n            List<User> users = usersByItemBought.get(item);\r\n            for (User user : users) {\r\n                List<Item> boughtTogether = user.itemsBought();\r\n                System.out.println(item + \" -> \" +boughtTogether);\r\n                for (Item i : boughtTogether) {\r\n                    association.associate(i);\r\n                }\r\n            }\r\n            associations.put(item, association);\r\n        }\r\n        return new ItemToItemRecommender(associations);\r\n    }\r\n    \r\n    public HashMap<Item, ItemToItemsAssociation> getAssociations() {\r\n        return associations;\r\n    }\r\n\r\n}\r\n',1947,44),(45,'package br.ime.usp.commendans.factory;\r\n\r\nimport java.util.ArrayList;\r\nimport java.util.Collections;\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\nimport java.util.Map;\r\nimport java.util.Set;\r\n\r\nimport br.ime.usp.commendans.model.Item;\r\n\r\npublic class ItemToItemsAssociation {\r\n    private Item item;\r\n    private Map<Item, Double> associations;\r\n\r\n    public ItemToItemsAssociation(Item item) {\r\n        this.item = item;\r\n        this.associations = new HashMap<Item, Double>();\r\n    }\r\n\r\n    public void associate(Item i) {\r\n        if (i.equals(this.item))\r\n            return;\r\n        Double v = associations.get(i);\r\n        if (v == null) {\r\n            v = 0.0;\r\n        }\r\n        v += 1.0;\r\n        associations.put(i, v);\r\n    }\r\n    \r\n    public double associationValue(Item i) {\r\n        Double v = associations.get(i);\r\n        if (v == null) {\r\n            return 0.0;\r\n        }\r\n        return v;\r\n    }\r\n    \r\n    \r\n    @Override\r\n    public String toString() {\r\n        String res = this.item.toString() + \" => {\" + associations.toString() + \"}\";\r\n        return res;\r\n    }\r\n\r\n    public List<Tuple> toTupleList() {\r\n        Set<Item> items = associations.keySet();\r\n        ArrayList<Tuple> tuples = new ArrayList<Tuple>();\r\n        for (Item item : items) {\r\n            Double value = associations.get(item);\r\n            tuples.add(new Tuple(item, value));\r\n        }\r\n        Collections.sort(tuples);\r\n        Collections.reverse(tuples);\r\n        return tuples;\r\n    }\r\n\r\n    \r\n    public class Tuple implements Comparable<Tuple> {\r\n        private Item item;\r\n        private Double value;\r\n\r\n        public Tuple(Item item, Double value) {\r\n            this.item = item;\r\n            this.value = value;\r\n        }\r\n\r\n        public Item getItem() {\r\n            return item;\r\n        }\r\n\r\n        public Double getValue() {\r\n            return value;\r\n        }\r\n\r\n        @Override\r\n        public int compareTo(Tuple o) {\r\n            return (int) (value - o.getValue());\r\n        }\r\n\r\n    }\r\n\r\n}\r\n',2037,45),(46,'package br.ime.usp.commendans.model;\r\n\r\npublic class Item {\r\n    private Long id;\r\n\r\n    public Item(Long id) {\r\n        this.id = id;\r\n    }\r\n    \r\n    @Override\r\n    public int hashCode() {\r\n        return id.hashCode();\r\n    }\r\n\r\n    @Override\r\n    public boolean equals(Object obj) {\r\n        if (obj instanceof Item) {\r\n            Item other = (Item) obj;\r\n            return other.getId().equals(this.id);\r\n        }\r\n        return false;\r\n    }\r\n    \r\n    public Long getId() {\r\n        return id;\r\n    }\r\n    \r\n    @Override\r\n    public String toString() {\r\n        return \"Item \" + id;\r\n    }\r\n}\r\n',608,46),(47,'package br.ime.usp.commendans.model;\r\n\r\nimport java.util.List;\r\n\r\npublic class User {\r\n\r\n    private final List<Item> items;\r\n\r\n    public User(List<Item> items) {\r\n        this.items = items;\r\n    }\r\n\r\n    public List<Item> itemsBought() {\r\n        return items;\r\n    }\r\n\r\n}\r\n',277,47),(48,'package br.ime.usp.commendans.factory;\r\n\r\nimport static org.junit.Assert.assertEquals;\r\n\r\nimport java.util.Arrays;\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\n\r\nimport org.junit.Test;\r\n\r\nimport br.ime.usp.commendans.ItemToItemRecommender;\r\nimport br.ime.usp.commendans.factory.ItemToItemsAssociation.Tuple;\r\nimport br.ime.usp.commendans.model.Item;\r\nimport br.ime.usp.commendans.model.User;\r\n\r\npublic class ItemToItemRecommenderFactoryTest {\r\n\r\n    @Test\r\n    public void test() {\r\n        ItemToItemRecommenderFactory factory = new ItemToItemRecommenderFactory();\r\n        Item item1 = new Item(1l);\r\n        Item item2 = new Item(2l);\r\n        Item item3 = new Item(3l);\r\n        Item item4 = new Item(4l);\r\n        User user1 = new User(Arrays.asList(item1, item3));\r\n        User user2 = new User(Arrays.asList(item2, item4));\r\n        User user3 = new User(Arrays.asList(item1, item2, item3));\r\n        User user4 = new User(Arrays.asList(item3, item4));\r\n        factory.addUser(user1);\r\n        factory.addUser(user2);\r\n        factory.addUser(user3);\r\n        factory.addUser(user4);\r\n        \r\n        ItemToItemRecommender recommender = factory.build();\r\n        HashMap<Item, ItemToItemsAssociation> associations = factory.getAssociations();\r\n        \r\n        List<Tuple> recommendedItems = recommender.recommendendItemsFor(item1);\r\n        ItemToItemsAssociation association = associations.get(item1);\r\n        assertEquals(0.0, association.associationValue(item1), 0.001);\r\n        assertEquals(1.0, association.associationValue(item2), 0.001);\r\n        assertEquals(2.0, association.associationValue(item3), 0.001);\r\n        assertEquals(0.0, association.associationValue(item4), 0.001);\r\n        \r\n        assertEquals(2, recommendedItems.size());\r\n        assertEquals(item3, recommendedItems.get(0).getItem());\r\n        assertEquals(2.0, recommendedItems.get(0).getValue().doubleValue(), 0.001);\r\n        assertEquals(item2, recommendedItems.get(1).getItem());\r\n        assertEquals(1.0, recommendedItems.get(1).getValue().doubleValue(), 0.001);\r\n        \r\n        recommendedItems = recommender.recommendendItemsFor(item2);\r\n        association = associations.get(item2);\r\n        assertEquals(1.0, association.associationValue(item1), 0.001);\r\n        assertEquals(0.0, association.associationValue(item2), 0.001);\r\n        assertEquals(1.0, association.associationValue(item3), 0.001);\r\n        assertEquals(1.0, association.associationValue(item4), 0.001);\r\n        \r\n        assertEquals(3, recommendedItems.size());\r\n        assertEquals(1.0, recommendedItems.get(0).getValue().doubleValue(), 0.001);\r\n        assertEquals(1.0, recommendedItems.get(1).getValue().doubleValue(), 0.001);\r\n        assertEquals(1.0, recommendedItems.get(2).getValue().doubleValue(), 0.001);\r\n        \r\n        recommendedItems = recommender.recommendendItemsFor(item3);\r\n        association = associations.get(item3);\r\n        assertEquals(2.0, association.associationValue(item1), 0.001);\r\n        assertEquals(1.0, association.associationValue(item2), 0.001);\r\n        assertEquals(0.0, association.associationValue(item3), 0.001);\r\n        assertEquals(1.0, association.associationValue(item4), 0.001);\r\n        \r\n        assertEquals(3, recommendedItems.size());\r\n        assertEquals(item1, recommendedItems.get(0).getItem());\r\n        assertEquals(2.0, recommendedItems.get(0).getValue().doubleValue(), 0.001);\r\n        assertEquals(1.0, recommendedItems.get(1).getValue().doubleValue(), 0.001);\r\n        assertEquals(1.0, recommendedItems.get(2).getValue().doubleValue(), 0.001);\r\n        \r\n        association = associations.get(item4);\r\n        assertEquals(0.0, association.associationValue(item1), 0.001);\r\n        assertEquals(1.0, association.associationValue(item2), 0.001);\r\n        assertEquals(1.0, association.associationValue(item3), 0.001);\r\n        assertEquals(0.0, association.associationValue(item4), 0.001);\r\n        \r\n    }\r\n\r\n}\r\n',3968,48),(49,'package br.ime.usp.commendans;\r\n\r\nimport java.util.List;\r\n\r\nimport br.ime.usp.commendans.itemtoitem.ItemToItemsAssociation.Tuple;\r\nimport br.ime.usp.commendans.model.Item;\r\n\r\npublic interface Recommender {\r\n    public List<Tuple> recommendendItemsFor(Item item);\r\n}\r\n',267,49),(50,'package br.ime.usp.commendans.itemtoitem;\r\n\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\nimport java.util.Map;\r\nimport java.util.Set;\r\n\r\nimport br.ime.usp.commendans.Recommender;\r\nimport br.ime.usp.commendans.itemtoitem.ItemToItemsAssociation.Tuple;\r\nimport br.ime.usp.commendans.model.Item;\r\n\r\npublic class ItemToItemRecommender implements Recommender {\r\n\r\n    private final Map<Item, List<Tuple>> associations;\r\n\r\n    public ItemToItemRecommender(\r\n            HashMap<Item, ItemToItemsAssociation> associations) {\r\n        this.associations = parse(associations);\r\n    }\r\n\r\n    private Map<Item, List<Tuple>> parse(HashMap<Item, ItemToItemsAssociation> associations) {\r\n        HashMap<Item, List<Tuple>> map = new HashMap<Item, List<Tuple>>();\r\n        Set<Item> items = associations.keySet();\r\n        for (Item item : items) {\r\n            ItemToItemsAssociation association = associations.get(item);\r\n            List<Tuple> tuples = association.toTupleList();\r\n            map.put(item, tuples);\r\n        }\r\n        return map;\r\n    }\r\n\r\n    @Override\r\n    public List<Tuple> recommendendItemsFor(Item item) {\r\n        List<Tuple> tuples = associations.get(item);\r\n        return tuples;\r\n    }\r\n\r\n}\r\n',1216,50),(51,'package br.ime.usp.commendans.itemtoitem;\r\n\r\nimport java.util.ArrayList;\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\nimport java.util.Map;\r\nimport java.util.Set;\r\n\r\nimport br.ime.usp.commendans.model.Item;\r\nimport br.ime.usp.commendans.model.User;\r\n\r\npublic class ItemToItemRecommenderFactory {\r\n\r\n    private Map<Item, List<User>> usersByItemBought;\r\n    private HashMap<Item, ItemToItemsAssociation> associations;\r\n    \r\n    public ItemToItemRecommenderFactory() {\r\n        usersByItemBought = new HashMap<Item, List<User>>();\r\n    }\r\n    \r\n    public void addUser(User user) {\r\n        List<Item> items = user.itemsBought();\r\n        for (Item item : items) {\r\n            store(item, user);\r\n        }\r\n    }\r\n\r\n    private void store(Item item, User user) {\r\n        List<User> users = usersByItemBought.get(item);\r\n        if (users == null) {\r\n            users = new ArrayList<User>();\r\n        }\r\n        users.add(user);\r\n        usersByItemBought.put(item, users);\r\n    }\r\n    \r\n    \r\n    public ItemToItemRecommender build() {\r\n        associations = new HashMap<Item, ItemToItemsAssociation>();\r\n        Set<Item> items = usersByItemBought.keySet();\r\n        for (Item item : items) {\r\n            ItemToItemsAssociation association = new ItemToItemsAssociation(item);\r\n            List<User> users = usersByItemBought.get(item);\r\n            for (User user : users) {\r\n                List<Item> boughtTogether = user.itemsBought();\r\n                System.out.println(item + \" -> \" +boughtTogether);\r\n                for (Item i : boughtTogether) {\r\n                    association.associate(i);\r\n                }\r\n            }\r\n            associations.put(item, association);\r\n        }\r\n        return new ItemToItemRecommender(associations);\r\n    }\r\n    \r\n    public HashMap<Item, ItemToItemsAssociation> getAssociations() {\r\n        return associations;\r\n    }\r\n\r\n}\r\n',1897,51),(52,'package br.ime.usp.commendans.itemtoitem;\r\n\r\nimport java.util.ArrayList;\r\nimport java.util.Collections;\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\nimport java.util.Map;\r\nimport java.util.Set;\r\n\r\nimport br.ime.usp.commendans.model.Item;\r\n\r\npublic class ItemToItemsAssociation {\r\n    private Item item;\r\n    private Map<Item, Double> associations;\r\n\r\n    public ItemToItemsAssociation(Item item) {\r\n        this.item = item;\r\n        this.associations = new HashMap<Item, Double>();\r\n    }\r\n\r\n    public void associate(Item i) {\r\n        if (i.equals(this.item))\r\n            return;\r\n        Double v = associations.get(i);\r\n        if (v == null) {\r\n            v = 0.0;\r\n        }\r\n        v += 1.0;\r\n        associations.put(i, v);\r\n    }\r\n    \r\n    public double associationValue(Item i) {\r\n        Double v = associations.get(i);\r\n        if (v == null) {\r\n            return 0.0;\r\n        }\r\n        return v;\r\n    }\r\n    \r\n    \r\n    @Override\r\n    public String toString() {\r\n        String res = this.item.toString() + \" => {\" + associations.toString() + \"}\";\r\n        return res;\r\n    }\r\n\r\n    public List<Tuple> toTupleList() {\r\n        Set<Item> items = associations.keySet();\r\n        ArrayList<Tuple> tuples = new ArrayList<Tuple>();\r\n        for (Item item : items) {\r\n            Double value = associations.get(item);\r\n            tuples.add(new Tuple(item, value));\r\n        }\r\n        Collections.sort(tuples);\r\n        Collections.reverse(tuples);\r\n        return tuples;\r\n    }\r\n\r\n    \r\n    public class Tuple implements Comparable<Tuple> {\r\n        private Item item;\r\n        private Double value;\r\n\r\n        public Tuple(Item item, Double value) {\r\n            this.item = item;\r\n            this.value = value;\r\n        }\r\n\r\n        public Item getItem() {\r\n            return item;\r\n        }\r\n\r\n        public Double getValue() {\r\n            return value;\r\n        }\r\n\r\n        @Override\r\n        public int compareTo(Tuple o) {\r\n            return (int) (value - o.getValue());\r\n        }\r\n\r\n    }\r\n\r\n}\r\n',2040,52),(53,'package br.ime.usp.commendans.model;\r\n\r\nimport javax.persistence.Entity;\r\nimport javax.persistence.Id;\r\n\r\n@Entity\r\npublic class Item {\r\n    @Id\r\n    private Long id;\r\n\r\n    @Deprecated\r\n    protected Item() {\r\n    }\r\n    \r\n    public Item(Long id) {\r\n        this.id = id;\r\n    }\r\n    \r\n    @Override\r\n    public int hashCode() {\r\n        return id.hashCode();\r\n    }\r\n\r\n    @Override\r\n    public boolean equals(Object obj) {\r\n        if (obj instanceof Item) {\r\n            Item other = (Item) obj;\r\n            return other.getId().equals(this.id);\r\n        }\r\n        return false;\r\n    }\r\n    \r\n    public Long getId() {\r\n        return id;\r\n    }\r\n    \r\n    @Override\r\n    public String toString() {\r\n        return \"Item \" + id;\r\n    }\r\n}\r\n',746,53),(54,'package br.ime.usp.commendans.model;\r\n\r\nimport java.util.List;\r\n\r\nimport javax.persistence.Entity;\r\nimport javax.persistence.GeneratedValue;\r\nimport javax.persistence.Id;\r\nimport javax.persistence.ManyToMany;\r\n\r\n@Entity\r\npublic class User {\r\n    \r\n    @Id @GeneratedValue\r\n    private Long id;\r\n\r\n    @ManyToMany\r\n    private List<Item> items;\r\n    \r\n    @Deprecated\r\n    public User() {\r\n    }\r\n\r\n    public User(List<Item> items, Long id) {\r\n        this.items = items;\r\n        this.id = id;\r\n    }\r\n\r\n    public List<Item> itemsBought() {\r\n        return items;\r\n    }\r\n    \r\n    @Override\r\n    public String toString() {\r\n        return \"User \" + id;\r\n    }\r\n\r\n    public void add(Item item) {\r\n        items.add(item);\r\n    }\r\n    \r\n    public List<Item> getItems() {\r\n        return items;\r\n    }\r\n\r\n}\r\n',810,54),(55,'package br.ime.usp.commendans.util;\r\n\r\nimport java.io.BufferedReader;\r\nimport java.io.IOException;\r\nimport java.io.InputStream;\r\nimport java.io.InputStreamReader;\r\nimport java.util.ArrayList;\r\nimport java.util.Arrays;\r\nimport java.util.Collection;\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\n\r\nimport org.apache.log4j.Logger;\r\nimport org.hibernate.SessionFactory;\r\nimport org.hibernate.StatelessSession;\r\nimport org.hibernate.cfg.Configuration;\r\n\r\nimport br.ime.usp.commendans.model.Item;\r\nimport br.ime.usp.commendans.model.User;\r\n\r\npublic class DataImporter {\r\n    \r\n    private final StatelessSession session;\r\n    private static Logger logger;\r\n\r\n    public DataImporter(StatelessSession session) {\r\n        this.session = session;\r\n    }\r\n\r\n    public static void main(String[] args) throws IOException {\r\n        logger = Logger.getLogger(DataImporter.class);\r\n        SessionFactory sf = new Configuration().configure(\"/hibernate.cfg.xml\").buildSessionFactory();\r\n        StatelessSession session = sf.openStatelessSession();\r\n        DataImporter dataImporter = new DataImporter(session);\r\n        dataImporter.importData(\"/orders.csv\");\r\n    }\r\n\r\n    private void importData(String file) throws IOException {\r\n        HashMap<Long, User> users = new HashMap<Long, User>();\r\n        HashMap<Long, Item> items = new HashMap<Long, Item>();\r\n        InputStream resourceAsStream = getClass().getResourceAsStream(file);\r\n        BufferedReader reader = new BufferedReader(new InputStreamReader(resourceAsStream));\r\n        String line = reader.readLine();\r\n        \r\n        while (line != null) {\r\n            logger.info(line);\r\n            List<String> row = Arrays.asList(line.split(\",\"));\r\n            long id = Long.parseLong(row.get(0));\r\n            long itemId = Long.parseLong(row.get(1).charAt(7) + \"\");\r\n            User user = users.get(id);\r\n            if (user == null) {\r\n                user = new User(new ArrayList<Item>(), id);\r\n            }\r\n            Item item = items.get(itemId);\r\n            if (item == null) {\r\n                item = new Item(itemId);\r\n            }\r\n            user.add(item);\r\n            line = reader.readLine();\r\n            users.put(id, user);\r\n            items.put(itemId, item);\r\n        }\r\n        \r\n        logger.info(\"persisting\");\r\n        session.getTransaction().begin();\r\n        for (Item item : items.values()) {\r\n            session.insert(item);\r\n        }\r\n        \r\n        Collection<User> allUsers = users.values();\r\n        for (User user : allUsers) {\r\n            session.insert(user);\r\n        }\r\n        session.getTransaction().commit();\r\n        logger.info(\"finished persisting\");\r\n    }\r\n\r\n}\r\n