>org.slf4j</groupId>\r\n		  <artifactId>slf4j-log4j12</artifactId>\r\n		  <version>1.6.1</version>\r\n		</dependency>\r\n		<dependency>\r\n		  <groupId>org.mortbay.jetty</groupId>\r\n		  <artifactId>servlet-api-2.5</artifactId>\r\n		  <version>6.1H.14.1</version>\r\n		</dependency>\r\n		<dependency>\r\n		  <groupId>org.glassfish.web</groupId>\r\n		  <artifactId>jstl-impl</artifactId>\r\n		  <version>1.2</version>\r\n		</dependency>\r\n		<dependency>\r\n		  <groupId>org.mortbay.jetty</groupId>\r\n		  <artifactId>servlet-api</artifactId>\r\n		  <version>3.0.pre4</version>\r\n		</dependency>\r\n		<dependency>\r\n		  <groupId>javassist</groupId>\r\n		  <artifactId>javassist</artifactId>\r\n		  <version>3.12.1.GA</version>\r\n		</dependency>\r\n		<dependency>\r\n		  <groupId>commons-io</groupId>\r\n		  <artifactId>commons-io</artifactId>\r\n		  <version>2.0.1</version>\r\n		</dependency>\r\n		<dependency>\r\n		  <groupId>com.google.guava</groupId>\r\n		  <artifactId>guava</artifactId>\r\n		  <version>r09</version>\r\n		</dependency>\r\n		<dependency>\r\n		  <groupId>com.google.code.gson</groupId>\r\n		  <artifactId>gson</artifactId>\r\n		  <version>2.2.2</version>\r\n		</dependency>\r\n		<dependency>\r\n		  <groupId>junit</groupId>\r\n		  <artifactId>junit</artifactId>\r\n		  <version>4.8.2</version>\r\n		</dependency>\r\n		<dependency>\r\n		  <groupId>org.mockito</groupId>\r\n		  <artifactId>mockito-all</artifactId>\r\n		  <version>1.8.5</version>\r\n		</dependency>\r\n		<dependency>\r\n		  <groupId>org.hsqldb</groupId>\r\n		  <artifactId>hsqldb</artifactId>\r\n		  <version>2.2.8</version>\r\n		</dependency>\r\n		<dependency>\r\n			<groupId>org.eclipse.jetty</groupId>\r\n			<artifactId>jetty-webapp</artifactId>\r\n			<version>7.4.4.v20110707</version>\r\n		</dependency>\r\n		<dependency>\r\n			<groupId>org.mortbay.jetty</groupId>\r\n			<artifactId>jsp-2.1-glassfish</artifactId>\r\n			<version>2.1.v20100127</version>\r\n		</dependency>\r\n		<dependency>\r\n			<groupId>postgresql</groupId>\r\n			<artifactId>postgresql</artifactId>\r\n			<version>9.1-901.jdbc4</version>\r\n		</dependency>\r\n            \r\n		<!-- Provided dependencies -->\r\n	</dependencies>\r\n\r\n	<reporting>\r\n		<plugins>\r\n			<plugin>\r\n				<groupId>org.codehaus.mojo</groupId>\r\n				<artifactId>cobertura-maven-plugin</artifactId>\r\n				<version>2.5.1</version>\r\n			</plugin>\r\n		</plugins>\r\n	</reporting>\r\n</project>\r\n',7253,205),(206,'',0,206),(207,'',0,207),(208,'',0,208),(209,'package br.ime.usp.commendans.controller;\r\n\r\nimport org.hibernate.Session;\r\n\r\nimport br.com.caelum.vraptor.Get;\r\nimport br.com.caelum.vraptor.Resource;\r\nimport br.com.caelum.vraptor.Result;\r\nimport br.com.caelum.vraptor.view.Results;\r\nimport br.ime.usp.commendans.infra.DataImporter;\r\n\r\n\r\n@Resource\r\npublic class IndexController {\r\n    \r\n    private final Result result;\r\n    private final Session session;\r\n\r\n    public IndexController(Result result, Session session) {\r\n        this.result = result;\r\n        this.session = session;\r\n    }\r\n    \r\n    @Get(\"/\")\r\n    public void index() {\r\n        result.use(Results.http()).body(\"<html><body><h1>hello world</h1</body></html>\");\r\n    }\r\n    \r\n    @Get(\"/import/askjdhakjshdakjshd\")\r\n    public void importData() {\r\n        DataImporter dataImporter = new DataImporter(session);\r\n        dataImporter.importData(\"/orders.csv\");\r\n        result.use(Results.http()).body(\"<html><body>finished persisting</body></html>\");\r\n    }\r\n}\r\n',981,209),(210,'package br.ime.usp.commendans.controller;\r\n\r\nimport java.util.ArrayList;\r\nimport java.util.List;\r\n\r\nimport br.com.caelum.vraptor.Get;\r\nimport br.com.caelum.vraptor.Resource;\r\nimport br.com.caelum.vraptor.Result;\r\nimport br.com.caelum.vraptor.view.Results;\r\nimport br.ime.usp.commendans.dao.ApplicationDao;\r\nimport br.ime.usp.commendans.dao.ItemDao;\r\nimport br.ime.usp.commendans.infra.TupleJsonSerializer;\r\nimport br.ime.usp.commendans.model.Application;\r\nimport br.ime.usp.commendans.model.Item;\r\nimport br.ime.usp.commendans.recommender.GeneralRecommender;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.ItemVector;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.Tuple;\r\n\r\n@Resource\r\npublic class ItemToItemController {\r\n    private final ItemDao itemDao;\r\n    private final Result result;\r\n    private final TupleJsonSerializer serializer;\r\n    private final GeneralRecommender recommender;\r\n    private final ApplicationDao appDao;\r\n\r\n    public ItemToItemController(GeneralRecommender recommender, \r\n            ItemDao itemDao, ApplicationDao appDao, Result result, \r\n            TupleJsonSerializer serializer) {\r\n        this.recommender = recommender;\r\n        this.itemDao = itemDao;\r\n        this.appDao = appDao;\r\n        this.result = result;\r\n        this.serializer = serializer;\r\n    }\r\n    \r\n    @Get(\"/recommend/item/{appItemId}\")\r\n    public void recommend(Long appItemId, String accessKey) {\r\n        if (validKey(accessKey)) {\r\n            Application app = appDao.findByAccessKey(accessKey);\r\n            Item item = itemDao.findByAppItemId(appItemId, app);\r\n            ItemVector recommended = recommender.recommendedItemsFor(item, accessKey);\r\n            serializeResult(recommended);\r\n        }\r\n    }\r\n    \r\n\r\n    @Get(\"/recommend/items/\")\r\n    public void recommend(List<Long> itemsIds, String accessKey) {\r\n        if (validKey(accessKey)) {\r\n            Application app = appDao.findByAccessKey(accessKey);\r\n            List<Item> items = itemDao.findAppItemIds(itemsIds, app);\r\n            ItemVector recommended = recommender.recommendedItemsFor(items, accessKey);\r\n            serializeResult(recommended);\r\n        }\r\n    }\r\n\r\n    private boolean validKey(String accessKey) {\r\n        Application app = appDao.findByAccessKey(accessKey);\r\n        if (app == null) {\r\n            result.notFound();\r\n            return false;\r\n        }\r\n        return true;\r\n    }\r\n\r\n    private void serializeResult(ItemVector recommended) {\r\n        List<Tuple> tuples = recommended == null ? new ArrayList<Tuple>() : recommended.getTuples();\r\n        result.use(Results.http())\r\n            .addHeader(\"content-type\", \"application/json\")\r\n            .body(serializer.toJson(tuples));\r\n    }\r\n}\r\n',2730,210),(211,'package br.ime.usp.commendans.infra;\r\n\r\nimport java.io.BufferedReader;\r\nimport java.io.IOException;\r\nimport java.io.InputStream;\r\nimport java.io.InputStreamReader;\r\nimport java.util.ArrayList;\r\nimport java.util.Arrays;\r\nimport java.util.Collection;\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\n\r\nimport org.apache.log4j.Logger;\r\nimport org.hibernate.Session;\r\nimport org.hibernate.SessionFactory;\r\nimport org.hibernate.cfg.Configuration;\r\n\r\nimport br.ime.usp.commendans.model.Application;\r\nimport br.ime.usp.commendans.model.Customer;\r\nimport br.ime.usp.commendans.model.Item;\r\n\r\npublic class DataImporter {\r\n    \r\n    private final Session session;\r\n    private static Logger logger = Logger.getLogger(DataImporter.class);\r\n\r\n    public DataImporter(Session session) {\r\n        this.session = session;\r\n    }\r\n\r\n    public static void main(String[] args) throws IOException {\r\n        SessionFactory sf = new Configuration().configure(\"/hibernate.cfg.xml\").buildSessionFactory();\r\n        Session session = sf.openSession();\r\n        DataImporter dataImporter = new DataImporter(session);\r\n        dataImporter.importData(\"/orders.csv\");\r\n    }\r\n\r\n    public void importData(String file) {\r\n        HashMap<Long, Customer> users = new HashMap<Long, Customer>();\r\n        HashMap<Long, Item> items = new HashMap<Long, Item>();\r\n        InputStream resourceAsStream = getClass().getResourceAsStream(file);\r\n        BufferedReader reader = new BufferedReader(new InputStreamReader(resourceAsStream));\r\n        String line = safeReadLine(reader);\r\n        Application cdc = new Application(\"Casa do Cdigo\", \"123\");\r\n        session.save(cdc);\r\n        \r\n        while (line != null) {\r\n            logger.debug(line);\r\n            List<String> row = Arrays.asList(line.split(\",\"));\r\n            long id = Long.parseLong(row.get(0));\r\n            long itemId = Long.parseLong(row.get(1).charAt(7) + \"\");\r\n            Customer user = users.get(id);\r\n            if (user == null) {\r\n                user = new Customer(new ArrayList<Item>(), id, cdc);\r\n            }\r\n            Item item = items.get(itemId);\r\n            if (item == null) {\r\n                item = new Item(itemId, cdc);\r\n            }\r\n            user.add(item);\r\n            line = safeReadLine(reader);\r\n            users.put(id, user);\r\n            items.put(itemId, item);\r\n        }\r\n        \r\n        logger.debug(\"persisting\");\r\n        session.getTransaction().begin();\r\n        for (Item item : items.values()) {\r\n            session.save(item);\r\n        }\r\n        \r\n        Collection<Customer> allUsers = users.values();\r\n        for (Customer user : allUsers) {\r\n            session.save(user);\r\n        }\r\n        session.getTransaction().commit();\r\n        logger.debug(\"finished persisting\");\r\n    }\r\n\r\n    private String safeReadLine(BufferedReader reader) {\r\n        try {\r\n            return reader.readLine();\r\n        } catch (IOException e) {\r\n            throw new RuntimeException(\"could not read file\", e);\r\n        }\r\n    }\r\n\r\n}\r\n',3031,211),(212,'package br.ime.usp.commendans.infra;\r\n\r\nimport org.eclipse.jetty.server.Server;\r\nimport org.eclipse.jetty.webapp.WebAppContext;\r\n\r\npublic class JettyRunner {\r\n    \r\n    public static void main(String[] args) throws Exception {\r\n        String webappDirLocation = \"src/main/webapp/\";\r\n        String webPort = System.getenv(\"PORT\");\r\n        if (webPort == null || webPort.isEmpty()) {\r\n            webPort = \"8080\";\r\n        }\r\n        Server server = new Server(Integer.valueOf(webPort));\r\n        WebAppContext root = new WebAppContext();\r\n        root.setContextPath(\"/\");\r\n        root.setDescriptor(webappDirLocation + \"/WEB-INF/web-heroku.xml\");\r\n        root.setResourceBase(webappDirLocation);\r\n        root.setParentLoaderPriority(true);\r\n        server.setHandler(root);\r\n        server.start();\r\n        server.join();\r\n    }\r\n    \r\n}\r\n',847,212),(213,'package br.ime.usp.commendans.infra;\r\n\r\nimport java.util.List;\r\n\r\nimport javax.annotation.PostConstruct;\r\n\r\nimport br.com.caelum.vraptor.ioc.ApplicationScoped;\r\nimport br.com.caelum.vraptor.ioc.Component;\r\nimport br.com.caelum.vraptor.ioc.ComponentFactory;\r\nimport br.ime.usp.commendans.dao.ApplicationDao;\r\nimport br.ime.usp.commendans.dao.CustomerDao;\r\nimport br.ime.usp.commendans.model.Application;\r\nimport br.ime.usp.commendans.model.Customer;\r\nimport br.ime.usp.commendans.recommender.GeneralRecommender;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.SingleAppRecommender;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.SingleAppRecommenderFactory;\r\n\r\n@Component @ApplicationScoped\r\npublic class RecommenderCreator implements ComponentFactory<GeneralRecommender> {\r\n    private final CustomerDao customerDao;\r\n    private final SingleAppRecommenderFactory factory;\r\n    private GeneralRecommender recommender;\r\n    private final ApplicationDao appDao;\r\n\r\n    public RecommenderCreator(CustomerDao dao, ApplicationDao appDao, SingleAppRecommenderFactory factory) {\r\n        this.customerDao = dao;\r\n        this.appDao = appDao;\r\n        this.factory = factory;\r\n    }\r\n\r\n    @Override\r\n    public GeneralRecommender getInstance() {\r\n        return recommender;\r\n    }\r\n    \r\n    @PostConstruct\r\n    public void create() {\r\n        List<Application> apps = appDao.list();\r\n        for (Application app : apps) {\r\n            List<Customer> customers = customerDao.findCustomersOf(app);\r\n            SingleAppRecommender recommender = buildSingleAppRecommender(customers);\r\n            app.use(recommender);\r\n        }\r\n        this.recommender = new GeneralRecommender(apps);\r\n    }\r\n\r\n    private SingleAppRecommender buildSingleAppRecommender(List<Customer> users) {\r\n        factory.clean();\r\n        for (Customer user : users) {\r\n            factory.addUser(user);\r\n        }\r\n        return factory.build();\r\n    }\r\n}\r\n',1943,213),(214,'package br.ime.usp.commendans.infra;\r\n\r\nimport javax.annotation.PostConstruct;\r\nimport javax.annotation.PreDestroy;\r\n\r\nimport org.hibernate.Session;\r\nimport org.hibernate.SessionFactory;\r\n\r\nimport br.com.caelum.vraptor.ioc.Component;\r\nimport br.com.caelum.vraptor.ioc.ComponentFactory;\r\nimport br.com.caelum.vraptor.ioc.RequestScoped;\r\n\r\n@Component @RequestScoped\r\npublic class SessionCreator implements ComponentFactory<Session> {\r\n    \r\n    private final SessionFactory sf;\r\n    private org.hibernate.classic.Session session;\r\n\r\n    public SessionCreator(SessionFactory sf) {\r\n        this.sf = sf;\r\n    }\r\n\r\n    @Override\r\n    public Session getInstance() {\r\n        return session;\r\n    }\r\n    \r\n    @PostConstruct\r\n    public void create() {\r\n        session = sf.openSession();\r\n    }\r\n    \r\n    @PreDestroy\r\n    public void destroy() {\r\n        session.close();\r\n    }\r\n\r\n}\r\n',882,214),(215,'package br.ime.usp.commendans.infra;\r\n\r\nimport java.net.URI;\r\nimport java.net.URISyntaxException;\r\n\r\nimport javax.annotation.PostConstruct;\r\nimport javax.annotation.PreDestroy;\r\nimport javax.servlet.ServletContext;\r\n\r\nimport org.apache.log4j.Logger;\r\nimport org.hibernate.SessionFactory;\r\nimport org.hibernate.cfg.Configuration;\r\n\r\nimport br.com.caelum.vraptor.ioc.ApplicationScoped;\r\nimport br.com.caelum.vraptor.ioc.Component;\r\nimport br.com.caelum.vraptor.ioc.ComponentFactory;\r\n\r\n@Component @ApplicationScoped\r\npublic class SessionFactoryCreator implements ComponentFactory<SessionFactory> {\r\n\r\n    private SessionFactory sessionFactory;\r\n    private static Logger LOG = Logger.getLogger(SessionFactoryCreator.class);\r\n    private String env;\r\n    \r\n    public SessionFactoryCreator(ServletContext context) {\r\n        env = context.getInitParameter(\"environment\");\r\n    }\r\n\r\n    @Override\r\n    public SessionFactory getInstance() {\r\n        return sessionFactory;\r\n    }\r\n    \r\n    @PreDestroy\r\n    public void destroy() {\r\n        sessionFactory.close();\r\n    }\r\n    \r\n    @PostConstruct\r\n    public void create() {\r\n        if (env.equals(\"heroku\")) {\r\n            \r\n            URI dbUri;\r\n            try {\r\n                dbUri = new URI(System.getenv(\"DATABASE_URL\"));\r\n            } catch (URISyntaxException e) {\r\n                throw new RuntimeException(e);\r\n            }\r\n\r\n            String username = dbUri.getUserInfo().split(\":\")[0];\r\n            String password = dbUri.getUserInfo().split(\":\")[1];\r\n            String dbUrl = \"jdbc:postgresql://\" + dbUri.getHost() + \':\' + dbUri.getPort()  + dbUri.getPath();\r\n            \r\n            LOG.info(\"using heroku specific confs\");\r\n            LOG.info(\"username: \" + username);\r\n            LOG.info(\"password: \" + password);\r\n            LOG.info(\"dbUrl: \" + dbUrl);\r\n            Configuration configuration = new Configuration().configure(\"/hibernate-heroku.cfg.xml\");\r\n            configuration.setProperty(\"hibernate.connection.url\", dbUrl);\r\n            configuration.setProperty(\"hibernate.connection.driver_class\", \"org.postgresql.Driver\");\r\n            configuration.setProperty(\"hibernate.dialect\", \"org.hibernate.dialect.PostgreSQLDialect\");\r\n            configuration.setProperty(\"hibernate.connection.username\", username);\r\n            configuration.setProperty(\"hibernate.connection.password\", password);\r\n            \r\n            sessionFactory = configuration.buildSessionFactory();\r\n        } else {\r\n            sessionFactory = new Configuration().configure(\"/hibernate.cfg.xml\").buildSessionFactory();\r\n        }\r\n    }\r\n\r\n}\r\n',2618,215),(216,'package br.ime.usp.commendans.infra;\r\n\r\nimport java.util.Arrays;\r\nimport java.util.List;\r\n\r\nimport br.com.caelum.vraptor.ioc.Component;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.Tuple;\r\n\r\nimport com.google.gson.ExclusionStrategy;\r\nimport com.google.gson.FieldAttributes;\r\nimport com.google.gson.Gson;\r\nimport com.google.gson.GsonBuilder;\r\n\r\n@Component\r\npublic class TupleJsonSerializer {\r\n    \r\n    private final Gson gson;\r\n\r\n    public TupleJsonSerializer() {\r\n        ExclusionStrategy strategy = new IncludeAllowedAttributes();\r\n        this.gson = new GsonBuilder().addSerializationExclusionStrategy(strategy).create();\r\n    }\r\n\r\n    public String toJson(Tuple tuple) {\r\n        return gson.toJson(tuple);\r\n    }\r\n    \r\n    public String toJson(List<Tuple> tuples) {\r\n        return gson.toJson(tuples);\r\n    }\r\n    \r\n    private class IncludeAllowedAttributes implements ExclusionStrategy {\r\n\r\n        private List<String> allowedAttributes = Arrays.asList(\"item\", \"appItemId\", \"value\");\r\n\r\n        @Override\r\n        public boolean shouldSkipClass(Class<?> arg0) {\r\n            return false;\r\n        }\r\n\r\n        @Override\r\n        public boolean shouldSkipField(FieldAttributes attr) {\r\n            String name = attr.getName();\r\n            boolean allowedAttribute = allowedAttributes.contains(name); \r\n            return !allowedAttribute;\r\n        }\r\n        \r\n    }\r\n\r\n    \r\n}\r\n',1403,216),(217,'',0,217),(218,'',0,218),(219,'',0,219),(220,'',0,220),(221,'',0,221),(222,'package br.ime.usp.commendans.model;\r\n\r\nimport java.util.List;\r\n\r\nimport javax.persistence.Entity;\r\nimport javax.persistence.GeneratedValue;\r\nimport javax.persistence.Id;\r\nimport javax.persistence.Transient;\r\n\r\nimport br.ime.usp.commendans.recommender.itemtoitem.ItemVector;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.SingleAppRecommender;\r\n\r\n@Entity\r\npublic class Application {\r\n\r\n\r\n    @Id\r\n    @GeneratedValue\r\n    private Long id;\r\n\r\n    private String name;\r\n\r\n    private String accessKey;\r\n\r\n    @Transient\r\n    private SingleAppRecommender recommender;\r\n\r\n    @Deprecated\r\n    protected Application() {\r\n    }\r\n\r\n    public Application(String name, String accessKey) {\r\n        this.name = name;\r\n        this.accessKey = accessKey;\r\n    }\r\n\r\n    public String getName() {\r\n        return name;\r\n    }\r\n\r\n    public void use(SingleAppRecommender recommender) {\r\n        this.recommender = recommender;\r\n    }\r\n\r\n    public Long getId() {\r\n        return id;\r\n    }\r\n\r\n    public ItemVector recommendedItemsFor(Item item) {\r\n        return recommender.recommendedItemsFor(item);\r\n    }\r\n\r\n    public ItemVector recommendedItemsFor(List<Item> items) {\r\n        return recommender.recommendedItemsFor(items);\r\n    }\r\n\r\n    public String getAccessKey() {\r\n        return accessKey;\r\n    }\r\n\r\n    @Override\r\n    public String toString() {\r\n        return \"Application [id=\" + id + \", name=\" + name + \", accessKey=\"\r\n                + accessKey + \"]\";\r\n    }\r\n    @Override\r\n    public int hashCode() {\r\n        final int prime = 31;\r\n        int result = 1;\r\n        result = prime * result + ((id == null) ? 0 : id.hashCode());\r\n        return result;\r\n    }\r\n    \r\n    @Override\r\n    public boolean equals(Object obj) {\r\n        if (this == obj)\r\n            return true;\r\n        if (obj == null)\r\n            return false;\r\n        if (getClass() != obj.getClass())\r\n            return false;\r\n        Application other = (Application) obj;\r\n        if (id == null) {\r\n            if (other.id != null)\r\n                return false;\r\n        } else if (!id.equals(other.id))\r\n            return false;\r\n        return true;\r\n    }\r\n}\r\n',2153,222),(223,'package br.ime.usp.commendans.recommender;\r\n\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\nimport java.util.Map;\r\n\r\nimport br.ime.usp.commendans.model.Application;\r\nimport br.ime.usp.commendans.model.Item;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.ItemVector;\r\n\r\npublic class GeneralRecommender {\r\n\r\n    private Map<String, Application> appsMap;\r\n\r\n    public GeneralRecommender(List<Application> apps) {\r\n        appsMap = new HashMap<String, Application>();\r\n        for (Application app : apps) {\r\n            appsMap.put(app.getAccessKey(), app);\r\n        }\r\n    }\r\n\r\n    public ItemVector recommendedItemsFor(Item item, String accessKey) {\r\n        Application application = appsMap.get(accessKey);\r\n        return application.recommendedItemsFor(item);\r\n    }\r\n\r\n    public ItemVector recommendedItemsFor(List<Item> items, String accessKey) {\r\n        Application application = appsMap.get(accessKey);\r\n        return application.recommendedItemsFor(items);\r\n    }\r\n\r\n}\r\n',994,223),(224,'package br.ime.usp.commendans.recommender;\r\n\r\nimport br.ime.usp.commendans.model.Item;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.ItemVector;\r\n\r\npublic interface Recommender {\r\n    public ItemVector recommendedItemsFor(Item item);\r\n}\r\n',245,224),(225,'package br.ime.usp.commendans.recommender.itemtoitem;\r\n\r\nimport java.util.ArrayList;\r\nimport java.util.Collections;\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\nimport java.util.Map;\r\nimport java.util.Set;\r\n\r\nimport br.ime.usp.commendans.model.Item;\r\n\r\npublic class ItemToItemsAssociation {\r\n    private Item item;\r\n    private Map<Item, Double> associations;\r\n\r\n    public ItemToItemsAssociation(Item item) {\r\n        this.item = item;\r\n        this.associations = new HashMap<Item, Double>();\r\n    }\r\n\r\n    public void associate(Item i) {\r\n        if (i.equals(this.item))\r\n            return;\r\n        Double v = associations.get(i);\r\n        if (v == null) {\r\n            v = 0.0;\r\n        }\r\n        v += 1.0;\r\n        associations.put(i, v);\r\n    }\r\n    \r\n    public double associationValue(Item i) {\r\n        Double v = associations.get(i);\r\n        if (v == null) {\r\n            return 0.0;\r\n        }\r\n        return v;\r\n    }\r\n    \r\n    \r\n    @Override\r\n    public String toString() {\r\n        String res = this.item.toString() + \" => {\" + associations.toString() + \"}\";\r\n        return res;\r\n    }\r\n\r\n    public List<Tuple> toTupleList() {\r\n        Set<Item> items = associations.keySet();\r\n        ArrayList<Tuple> tuples = new ArrayList<Tuple>();\r\n        for (Item item : items) {\r\n            Double value = associations.get(item);\r\n            tuples.add(new Tuple(item, value));\r\n        }\r\n        Collections.sort(tuples);\r\n        Collections.reverse(tuples);\r\n        return tuples;\r\n    }\r\n\r\n}\r\n',1524,225),(226,'package br.ime.usp.commendans.recommender.itemtoitem;\r\n\r\nimport java.util.ArrayList;\r\nimport java.util.Collections;\r\nimport java.util.HashSet;\r\nimport java.util.List;\r\nimport java.util.Set;\r\n\r\npublic class ItemVector {\r\n\r\n    private Set<Tuple> tuples;\r\n\r\n    public ItemVector(Set<Tuple> tuples) {\r\n        this.tuples = tuples;\r\n    }\r\n\r\n    public ItemVector(List<Tuple> tuples) {\r\n        this(new HashSet<Tuple>(tuples));\r\n    }\r\n\r\n    public List<Tuple> getTuples() {\r\n        ArrayList<Tuple> tuples = new ArrayList<Tuple>(this.tuples);\r\n        Collections.sort(tuples);\r\n        Collections.reverse(tuples);\r\n        return Collections.unmodifiableList(tuples);\r\n    }\r\n    \r\n    public ItemVector merge(ItemVector other) {\r\n        Set<Tuple> tuples = new HashSet<Tuple>();\r\n        Set<Tuple> tuplesToRemove = new HashSet<Tuple>();\r\n        for (Tuple tuple : this.tuples) {\r\n            for (Tuple t : other.getTuples()) {\r\n                if (t.getItem().equals(tuple.getItem())) {\r\n                    Tuple newTuple = new Tuple(t.getItem(), tuple.getValue() + t.getValue());\r\n                    tuples.add(newTuple);\r\n                    tuplesToRemove.add(t);\r\n                    tuplesToRemove.add(tuple);\r\n                    break;\r\n                }\r\n            }\r\n        }\r\n        tuples.addAll(this.tuples);\r\n        tuples.addAll(other.getTuples());\r\n        tuples.removeAll(tuplesToRemove);\r\n        return new ItemVector(tuples);\r\n    }\r\n\r\n}\r\n',1474,226),(227,'package br.ime.usp.commendans.recommender.itemtoitem;\r\n\r\nimport java.util.ArrayList;\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\nimport java.util.Map;\r\nimport java.util.Set;\r\n\r\nimport br.ime.usp.commendans.model.Item;\r\nimport br.ime.usp.commendans.recommender.Recommender;\r\n\r\npublic class SingleAppRecommender implements Recommender {\r\n\r\n    private final Map<Item, ItemVector> associations;\r\n\r\n    public SingleAppRecommender(\r\n            HashMap<Item, ItemToItemsAssociation> associations) {\r\n        this.associations = parse(associations);\r\n    }\r\n\r\n    private Map<Item, ItemVector> parse(HashMap<Item, ItemToItemsAssociation> associations) {\r\n        HashMap<Item, ItemVector> map = new HashMap<Item, ItemVector>();\r\n        Set<Item> items = associations.keySet();\r\n        for (Item item : items) {\r\n            ItemToItemsAssociation association = associations.get(item);\r\n            List<Tuple> tuples = association.toTupleList();\r\n            map.put(item, new ItemVector(tuples));\r\n        }\r\n        return map;\r\n    }\r\n\r\n    @Override\r\n    public ItemVector recommendedItemsFor(Item item) {\r\n        return associations.get(item);\r\n    }\r\n\r\n    public ItemVector recommendedItemsFor(List<Item> items) {\r\n        ItemVector vector = new ItemVector(new ArrayList<Tuple>());\r\n        for (Item item : items) {\r\n            ItemVector other = recommendedItemsFor(item);\r\n            vector = vector.merge(other);\r\n        }\r\n        return vector;\r\n    }\r\n\r\n}\r\n',1481,227),(228,'package br.ime.usp.commendans.recommender.itemtoitem;\r\n\r\nimport java.util.ArrayList;\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\nimport java.util.Map;\r\nimport java.util.Set;\r\n\r\nimport br.com.caelum.vraptor.ioc.ApplicationScoped;\r\nimport br.com.caelum.vraptor.ioc.Component;\r\nimport br.ime.usp.commendans.model.Customer;\r\nimport br.ime.usp.commendans.model.Item;\r\n\r\n@Component @ApplicationScoped\r\npublic class SingleAppRecommenderFactory {\r\n\r\n    private Map<Item, List<Customer>> customerByItemBought;\r\n    private HashMap<Item, ItemToItemsAssociation> associations;\r\n    \r\n    public SingleAppRecommenderFactory() {\r\n        customerByItemBought = new HashMap<Item, List<Customer>>();\r\n    }\r\n    \r\n    public void addUser(Customer user) {\r\n        for (Item item : user.itemsBought()) {\r\n            store(item, user);\r\n        }\r\n    }\r\n\r\n    private void store(Item item, Customer user) {\r\n        List<Customer> users = customerByItemBought.get(item);\r\n        if (users == null) {\r\n            users = new ArrayList<Customer>();\r\n        }\r\n        users.add(user);\r\n        customerByItemBought.put(item, users);\r\n    }\r\n    \r\n    \r\n    public SingleAppRecommender build() {\r\n        associations = new HashMap<Item, ItemToItemsAssociation>();\r\n        Set<Item> items = customerByItemBought.keySet();\r\n        for (Item item : items) {\r\n            associate(item);\r\n        }\r\n        return new SingleAppRecommender(associations);\r\n    }\r\n\r\n    private void associate(Item item) {\r\n        ItemToItemsAssociation association = new ItemToItemsAssociation(item);\r\n        List<Customer> customers = customerByItemBought.get(item);\r\n        for (Customer user : customers) {\r\n            List<Item> boughtTogether = user.itemsBought();\r\n            for (Item i : boughtTogether) {\r\n                association.associate(i);\r\n            }\r\n        }\r\n        associations.put(item, association);\r\n    }\r\n    \r\n    public HashMap<Item, ItemToItemsAssociation> getAssociations() {\r\n        return associations;\r\n    }\r\n\r\n    public void clean() {\r\n        customerByItemBought = new HashMap<Item, List<Customer>>();\r\n    }\r\n\r\n}\r\n',2142,228),(229,'package br.ime.usp.commendans.recommender.itemtoitem;\r\n\r\nimport br.ime.usp.commendans.model.Item;\r\n\r\npublic class Tuple implements Comparable<Tuple> {\r\n    private Item item;\r\n    private Double value;\r\n\r\n    public Tuple(Item item, Double value) {\r\n        this.item = item;\r\n        this.value = value;\r\n    }\r\n\r\n    public Item getItem() {\r\n        return item;\r\n    }\r\n\r\n    public Double getValue() {\r\n        return value;\r\n    }\r\n\r\n    @Override\r\n    public int compareTo(Tuple o) {\r\n        return (int) (value - o.getValue());\r\n    }\r\n    \r\n    @Override\r\n    public int hashCode() {\r\n        final int prime = 31;\r\n        int result = 1;\r\n        result = prime * result + ((item == null) ? 0 : item.hashCode());\r\n        result = prime * result + ((value == null) ? 0 : value.hashCode());\r\n        return result;\r\n    }\r\n\r\n    @Override\r\n    public boolean equals(Object obj) {\r\n        if (this == obj)\r\n            return true;\r\n        if (obj == null)\r\n            return false;\r\n        if (getClass() != obj.getClass())\r\n            return false;\r\n        Tuple other = (Tuple) obj;\r\n        if (item == null) {\r\n            if (other.item != null)\r\n                return false;\r\n        } else if (!item.equals(other.item))\r\n            return false;\r\n        if (value == null) {\r\n            if (other.value != null)\r\n                return false;\r\n        } else if (!value.equals(other.value))\r\n            return false;\r\n        return true;\r\n    }\r\n\r\n    @Override\r\n    public String toString() {\r\n        return \"[Tuple: item=\" + item + \"]\";\r\n    }\r\n\r\n}\r\n',1582,229),(230,'',0,230),(231,'',0,231),(232,'package br.ime.usp.commendans.components;\r\n\r\nimport static org.mockito.Mockito.mock;\r\nimport static org.mockito.Mockito.when;\r\n\r\nimport javax.servlet.ServletContext;\r\n\r\nimport org.junit.Ignore;\r\nimport org.junit.Test;\r\n\r\nimport br.ime.usp.commendans.infra.SessionFactoryCreator;\r\n\r\npublic class SessionFactoryCreatorTest {\r\n\r\n    @Test @Ignore\r\n    public void shouldGetSessionFactory() {\r\n        ServletContext context = mock(ServletContext.class);\r\n        when(context.getInitParameter(\"environment\")).thenReturn(\"heroku\");\r\n        SessionFactoryCreator sfc = new SessionFactoryCreator(context);\r\n        sfc.create();\r\n    }\r\n\r\n}\r\n',637,232),(233,'',0,233),(234,'package br.ime.usp.commendans.infra;\r\n\r\nimport static org.junit.Assert.assertEquals;\r\n\r\nimport java.util.Arrays;\r\n\r\nimport org.junit.Test;\r\n\r\nimport br.ime.usp.commendans.model.Application;\r\nimport br.ime.usp.commendans.model.Item;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.Tuple;\r\n\r\npublic class TupleJsonSerializerTest {\r\n\r\n    @Test\r\n    public void shouldSerializeATuple() {\r\n        TupleJsonSerializer serializer = new TupleJsonSerializer();\r\n        Application cdc = new Application(\"Casa do Cdigo\", \"123\");\r\n        String json = serializer.toJson(new Tuple(new Item(1l, cdc), 10.0));\r\n        assertEquals(\"{\\\"item\\\":{\\\"appItemId\\\":1},\\\"value\\\":10.0}\", json);\r\n    }\r\n    \r\n    @Test\r\n    public void shouldSerializeATupleList() {\r\n        TupleJsonSerializer serializer = new TupleJsonSerializer();\r\n        Application cdc = new Application(\"Casa do Cdigo\", \"123\");\r\n        String json = serializer.toJson(Arrays.asList(new Tuple(new Item(1l, cdc), 10.0)));\r\n        assertEquals(\"[{\\\"item\\\":{\\\"appItemId\\\":1},\\\"value\\\":10.0}]\", json);\r\n    }\r\n\r\n}\r\n',1075,234),(235,'',0,235),(236,'package br.ime.usp.commendans.recommender.itemtoitem;\r\n\r\nimport static org.junit.Assert.assertEquals;\r\n\r\nimport java.util.ArrayList;\r\nimport java.util.HashSet;\r\nimport java.util.List;\r\n\r\nimport org.junit.Test;\r\n\r\nimport br.ime.usp.commendans.model.Application;\r\nimport br.ime.usp.commendans.model.Item;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.ItemVector;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.Tuple;\r\n\r\npublic class ItemVectorTest {\r\n\r\n    @Test\r\n    public void shouldMergeTuples() {\r\n        Application cdc = new Application(\"Casa do Cdigo\", \"123\");\r\n        Item item1 = new Item(1l, cdc);\r\n        Item item2 = new Item(2l, cdc);\r\n        Item item3 = new Item(3l, cdc);\r\n        Item item4 = new Item(4l, cdc);\r\n        List<Tuple> tuples = asList(new Tuple(item1, 11.0), new Tuple(item2, 8.0), new Tuple(item3, 2.0));\r\n        ItemVector itemVector1 = new ItemVector(new HashSet<Tuple>(tuples));\r\n        tuples = asList(new Tuple(item4, 10.0), new Tuple(item3, 7.0));\r\n        ItemVector itemVector2 = new ItemVector(new HashSet<Tuple>(tuples));\r\n        \r\n        ItemVector merge = itemVector1.merge(itemVector2);\r\n        List<Tuple> expected = asList(new Tuple(item1, 11.0), new Tuple(item4, 10.0), new Tuple(item3, 9.0), new Tuple(item2, 8.0));\r\n        assertEquals(expected, merge.getTuples());\r\n        \r\n    }\r\n\r\n    private List<Tuple> asList(Tuple ... tuples) {\r\n        ArrayList<Tuple> tuplesList = new ArrayList<Tuple>();\r\n        for (int i = 0; i < tuples.length; i++) {\r\n            tuplesList.add(tuples[i]);\r\n        }\r\n        return tuplesList;\r\n    }\r\n\r\n}\r\n',1616,236),(237,'package br.ime.usp.commendans.recommender.itemtoitem;\r\n\r\nimport static org.junit.Assert.assertEquals;\r\n\r\nimport java.util.Arrays;\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\n\r\nimport org.junit.Test;\r\n\r\nimport br.ime.usp.commendans.model.Application;\r\nimport br.ime.usp.commendans.model.Customer;\r\nimport br.ime.usp.commendans.model.Item;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.ItemToItemsAssociation;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.SingleAppRecommender;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.SingleAppRecommenderFactory;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.Tuple;\r\n\r\npublic class SingleAppRecommenderFactoryTest {\r\n\r\n    @Test\r\n    public void shouldBuildRecommenderFromCollectiveIntelligenceInActionExample() {\r\n        SingleAppRecommenderFactory factory = new SingleAppRecommenderFactory();\r\n        Application cdc = new Application(\"casadocodigo\", \"1234\");\r\n        Item item1 = new Item(1l, cdc);\r\n        Item item2 = new Item(2l, cdc);\r\n        Item item3 = new Item(3l, cdc);\r\n        Item item4 = new Item(4l, cdc);\r\n        Customer user1 = new Customer(Arrays.asList(item1, item3), 1l, cdc);\r\n        Customer user2 = new Customer(Arrays.asList(item2, item4), 2l, cdc);\r\n        Customer user3 = new Customer(Arrays.asList(item1, item2, item3), 3l, cdc);\r\n        Customer user4 = new Customer(Arrays.asList(item3, item4), 4l, cdc);\r\n        factory.addUser(user1);\r\n        factory.addUser(user2);\r\n        factory.addUser(user3);\r\n        factory.addUser(user4);\r\n        \r\n        SingleAppRecommender recommender = factory.build();\r\n        HashMap<Item, ItemToItemsAssociation> associations = factory.getAssociations();\r\n        \r\n        List<Tuple> recommendedItems = recommender.recommendedItemsFor(item1).getTuples();\r\n        ItemToItemsAssociation association = associations.get(item1);\r\n        assertEquals(0.0, association.associationValue(item1), 0.001);\r\n        assertEquals(1.0, association.associationValue(item2), 0.001);\r\n        assertEquals(2.0, association.associationValue(item3), 0.001);\r\n        assertEquals(0.0, association.associationValue(item4), 0.001);\r\n        \r\n        assertEquals(2, recommendedItems.size());\r\n        assertEquals(item3, recommendedItems.get(0).getItem());\r\n        assertEquals(2.0, recommendedItems.get(0).getValue().doubleValue(), 0.001);\r\n        assertEquals(item2, recommendedItems.get(1).getItem());\r\n        assertEquals(1.0, recommendedItems.get(1).getValue().doubleValue(), 0.001);\r\n        \r\n        recommendedItems = recommender.recommendedItemsFor(item2).getTuples();\r\n        association = associations.get(item2);\r\n        assertEquals(1.0, association.associationValue(item1), 0.001);\r\n        assertEquals(0.0, association.associationValue(item2), 0.001);\r\n        assertEquals(1.0, association.associationValue(item3), 0.001);\r\n        assertEquals(1.0, association.associationValue(item4), 0.001);\r\n        \r\n        assertEquals(3, recommendedItems.size());\r\n        assertEquals(1.0, recommendedItems.get(0).getValue().doubleValue(), 0.001);\r\n        assertEquals(1.0, recommendedItems.get(1).getValue().doubleValue(), 0.001);\r\n        assertEquals(1.0, recommendedItems.get(2).getValue().doubleValue(), 0.001);\r\n        \r\n        recommendedItems = recommender.recommendedItemsFor(item3).getTuples();\r\n        association = associations.get(item3);\r\n        assertEquals(2.0, association.associationValue(item1), 0.001);\r\n        assertEquals(1.0, association.associationValue(item2), 0.001);\r\n        assertEquals(0.0, association.associationValue(item3), 0.001);\r\n        assertEquals(1.0, association.associationValue(item4), 0.001);\r\n        \r\n        assertEquals(3, recommendedItems.size());\r\n        assertEquals(item1, recommendedItems.get(0).getItem());\r\n        assertEquals(2.0, recommendedItems.get(0).getValue().doubleValue(), 0.001);\r\n        assertEquals(1.0, recommendedItems.get(1).getValue().doubleValue(), 0.001);\r\n        assertEquals(1.0, recommendedItems.get(2).getValue().doubleValue(), 0.001);\r\n        \r\n        association = associations.get(item4);\r\n        assertEquals(0.0, association.associationValue(item1), 0.001);\r\n        assertEquals(1.0, association.associationValue(item2), 0.001);\r\n        assertEquals(1.0, association.associationValue(item3), 0.001);\r\n        assertEquals(0.0, association.associationValue(item4), 0.001);\r\n        \r\n    }\r\n\r\n}\r\n',4453,237),(238,'<?xml version=\"1.0\" encoding=\"UTF-8\" ?>\r\n<!DOCTYPE log4j:configuration SYSTEM \"log4j.dtd\">\r\n<log4j:configuration xmlns:log4j=\"http://jakarta.apache.org/log4j/\">\r\n\r\n	<appender name=\"stdout\" class=\"org.apache.log4j.ConsoleAppender\">\r\n		<layout class=\"org.apache.log4j.PatternLayout\">\r\n			<param name=\"ConversionPattern\" value=\"%d{HH:mm:ss,SSS} %5p [%-20c{1}] %m%n\" />\r\n		</layout>\r\n	</appender>\r\n\r\n	<category name=\"org.hibernate\">\r\n		<priority value=\"DEBUG\" />\r\n		<appender-ref ref=\"stdout\" />\r\n	</category>\r\n	\r\n	<category name=\"br.ime.usp\">\r\n		<priority value=\"INFO\" />\r\n		<appender-ref ref=\"stdout\" />\r\n	</category>\r\n\r\n	<category name=\"br.com.caelum.vraptor\">\r\n		<priority value=\"INFO\" />\r\n		<appender-ref ref=\"stdout\" />\r\n	</category>\r\n	\r\n	<category name=\"org.eclipse.jetty.util.log\">\r\n		<priority value=\"DEBUG\" />\r\n		<appender-ref ref=\"stdout\" />\r\n	</category>\r\n	\r\n\r\n</log4j:configuration>\r\n',893,238),(239,'package br.ime.usp.commendans.dao;\r\n\r\nimport java.util.List;\r\n\r\nimport org.hibernate.Query;\r\nimport org.hibernate.Session;\r\n\r\nimport br.com.caelum.vraptor.ioc.Component;\r\nimport br.com.caelum.vraptor.ioc.PrototypeScoped;\r\nimport br.ime.usp.commendans.model.Application;\r\nimport br.ime.usp.commendans.model.Customer;\r\n\r\n@Component @PrototypeScoped\r\npublic class CustomerDao {\r\n    private final Session session;\r\n\r\n    public CustomerDao(Session session) {\r\n        this.session = session;\r\n    }\r\n    \r\n    @SuppressWarnings(\"unchecked\")\r\n    public List<Customer> list() {\r\n        return session.createCriteria(Customer.class).list();\r\n    }\r\n\r\n    @SuppressWarnings(\"unchecked\")\r\n    public List<Customer> findCustomersOf(Application app) {\r\n        Query query = session.createQuery(\"select customer from Customer customer \" +\r\n        		\"join fetch customer.items \" +\r\n        		\"where customer.app.name like :appName\");\r\n        query.setParameter(\"appName\", app.getName());\r\n        return query.list();\r\n    }\r\n}\r\n',1022,239),(240,'package br.ime.usp.commendans.infra;\r\n\r\nimport java.util.List;\r\n\r\nimport javax.annotation.PostConstruct;\r\n\r\nimport org.hibernate.Session;\r\nimport org.hibernate.SessionFactory;\r\n\r\nimport br.com.caelum.vraptor.ioc.ApplicationScoped;\r\nimport br.com.caelum.vraptor.ioc.Component;\r\nimport br.com.caelum.vraptor.ioc.ComponentFactory;\r\nimport br.ime.usp.commendans.dao.ApplicationDao;\r\nimport br.ime.usp.commendans.dao.CustomerDao;\r\nimport br.ime.usp.commendans.model.Application;\r\nimport br.ime.usp.commendans.model.Customer;\r\nimport br.ime.usp.commendans.recommender.GeneralRecommender;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.SingleAppRecommender;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.SingleAppRecommenderFactory;\r\n\r\n@Component @ApplicationScoped\r\npublic class RecommenderCreator implements ComponentFactory<GeneralRecommender> {\r\n    private final CustomerDao customerDao;\r\n    private final SingleAppRecommenderFactory factory;\r\n    private GeneralRecommender recommender;\r\n    private final ApplicationDao appDao;\r\n\r\n    public RecommenderCreator(SingleAppRecommenderFactory factory, SessionFactory sf) {\r\n        Session session = sf.openSession();\r\n        this.customerDao = new CustomerDao(session);\r\n        this.appDao = new ApplicationDao(session);\r\n        this.factory = factory;\r\n    }\r\n\r\n    @Override\r\n    public GeneralRecommender getInstance() {\r\n        return recommender;\r\n    }\r\n    \r\n    @PostConstruct\r\n    public void create() {\r\n        List<Application> apps = appDao.list();\r\n        for (Application app : apps) {\r\n            List<Customer> customers = customerDao.findCustomersOf(app);\r\n            SingleAppRecommender recommender = buildSingleAppRecommender(customers);\r\n            app.use(recommender);\r\n        }\r\n        this.recommender = new GeneralRecommender(apps);\r\n    }\r\n\r\n    private SingleAppRecommender buildSingleAppRecommender(List<Customer> users) {\r\n        factory.clean();\r\n        for (Customer user : users) {\r\n            factory.addUser(user);\r\n        }\r\n        return factory.build();\r\n    }\r\n}\r\n',2080,240),(241,'package br.ime.usp.commendans.controller;\r\n\r\nimport org.hibernate.Session;\r\n\r\nimport br.com.caelum.vraptor.Get;\r\nimport br.com.caelum.vraptor.Resource;\r\nimport br.com.caelum.vraptor.Result;\r\nimport br.com.caelum.vraptor.view.Results;\r\nimport br.ime.usp.commendans.infra.DataImporter;\r\nimport br.ime.usp.commendans.recommender.RecommenderCreator;\r\n\r\n\r\n@Resource\r\npublic class IndexController {\r\n    \r\n    private final Result result;\r\n    private final Session session;\r\n    private final RecommenderCreator recommenderCreator;\r\n\r\n    public IndexController(Result result, Session session, RecommenderCreator recommenderCreator) {\r\n        this.result = result;\r\n        this.session = session;\r\n        this.recommenderCreator = recommenderCreator;\r\n    }\r\n    \r\n    @Get(\"/\")\r\n    public void index() {\r\n        result.use(Results.http()).body(\"<html><body><h1>hello world</h1</body></html>\");\r\n    }\r\n    \r\n    @Get(\"/import/askjdhakjshdakjshd\")\r\n    public void importData() {\r\n        DataImporter dataImporter = new DataImporter(session);\r\n        dataImporter.importData(\"/orders.csv\");\r\n        recommenderCreator.create();\r\n        result.use(Results.http()).body(\"<html><body>finished persisting</body></html>\");\r\n    }\r\n}\r\n',1233,241),(242,'',0,242),(243,'package br.ime.usp.commendans.recommender;\r\n\r\nimport java.util.List;\r\n\r\nimport javax.annotation.PostConstruct;\r\n\r\nimport org.hibernate.Session;\r\nimport org.hibernate.SessionFactory;\r\n\r\nimport br.com.caelum.vraptor.ioc.ApplicationScoped;\r\nimport br.com.caelum.vraptor.ioc.Component;\r\nimport br.com.caelum.vraptor.ioc.ComponentFactory;\r\nimport br.ime.usp.commendans.dao.ApplicationDao;\r\nimport br.ime.usp.commendans.dao.CustomerDao;\r\nimport br.ime.usp.commendans.model.Application;\r\nimport br.ime.usp.commendans.model.Customer;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.SingleAppRecommender;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.SingleAppRecommenderFactory;\r\n\r\n@Component @ApplicationScoped\r\npublic class RecommenderCreator implements ComponentFactory<GeneralRecommender> {\r\n    private final CustomerDao customerDao;\r\n    private final SingleAppRecommenderFactory factory;\r\n    private GeneralRecommender recommender;\r\n    private final ApplicationDao appDao;\r\n\r\n    public RecommenderCreator(SingleAppRecommenderFactory factory, SessionFactory sf) {\r\n        Session session = sf.openSession();\r\n        this.customerDao = new CustomerDao(session);\r\n        this.appDao = new ApplicationDao(session);\r\n        this.factory = factory;\r\n    }\r\n\r\n    @Override\r\n    public GeneralRecommender getInstance() {\r\n        return recommender;\r\n    }\r\n    \r\n    @PostConstruct\r\n    public void create() {\r\n        List<Application> apps = appDao.list();\r\n        for (Application app : apps) {\r\n            List<Customer> customers = customerDao.findCustomersOf(app);\r\n            SingleAppRecommender recommender = buildSingleAppRecommender(customers);\r\n            app.use(recommender);\r\n        }\r\n        this.recommender = new GeneralRecommender(apps);\r\n    }\r\n    \r\n    private SingleAppRecommender buildSingleAppRecommender(List<Customer> users) {\r\n        factory.clean();\r\n        for (Customer user : users) {\r\n            factory.addUser(user);\r\n        }\r\n        return factory.build();\r\n    }\r\n}\r\n',2028,243),(244,'<?xml version=\'1.0\' encoding=\'utf-8\'?>\r\n<!DOCTYPE hibernate-configuration PUBLIC\r\n        \"-//Hibernate/Hibernate Configuration DTD//EN\"\r\n        \"http://www.hibernate.org/dtd/hibernate-configuration-3.0.dtd\">\r\n<hibernate-configuration>\r\n    <session-factory>\r\n    \r\n    	<!-- connection pool configs -->\r\n<!-- 	    <property name=\"hibernate.c3p0.acquire_increment\">1</property>  -->\r\n<!-- 		<property name=\"hibernate.c3p0.idle_test_period\">100</property> seconds  -->\r\n<!-- 		<property name=\"hibernate.c3p0.max_size\">15</property>  -->\r\n<!-- 		<property name=\"hibernate.c3p0.min_size\">1</property>  -->\r\n<!-- 		<property name=\"hibernate.c3p0.timeout\">600</property> -->\r\n    \r\n        <property name=\"show_sql\">false</property>\r\n        <property name=\"format_sql\">false</property>\r\n        <property name=\"hibernate.hbm2ddl.auto\">update</property>\r\n\r\n        <mapping class=\"br.ime.usp.commendans.model.Item\" />\r\n        <mapping class=\"br.ime.usp.commendans.model.Customer\" />\r\n        <mapping class=\"br.ime.usp.commendans.model.Application\" />\r\n\r\n    </session-factory>\r\n</hibernate-configuration>\r\n',1104,244),(245,'curl --data \"clientAppUserId=66666666&itemsIds[0]=1&itemsIds[1]=2&itemsIds[2]=3\" http://localhost:8080/commendans/app/123/addSale\r\n',131,245),(246,'package br.ime.usp.commendans.controller;\r\n\r\nimport java.util.ArrayList;\r\nimport java.util.List;\r\n\r\nimport br.com.caelum.vraptor.Post;\r\nimport br.com.caelum.vraptor.Resource;\r\nimport br.com.caelum.vraptor.Result;\r\nimport br.com.caelum.vraptor.view.Results;\r\nimport br.ime.usp.commendans.dao.ClientAppDao;\r\nimport br.ime.usp.commendans.dao.CustomerDao;\r\nimport br.ime.usp.commendans.dao.ItemDao;\r\nimport br.ime.usp.commendans.model.ClientApp;\r\nimport br.ime.usp.commendans.model.Customer;\r\nimport br.ime.usp.commendans.model.GroupedItems;\r\nimport br.ime.usp.commendans.model.Item;\r\n\r\n@Resource\r\npublic class ClientAppController {\r\n\r\n    private final Result result;\r\n    private final ClientAppDao appDao;\r\n    private final ItemDao itemDao;\r\n    private final CustomerDao customerDao;\r\n\r\n    public ClientAppController(Result result, ClientAppDao appDao, ItemDao itemDao, CustomerDao customerDao) {\r\n        this.result = result;\r\n        this.appDao = appDao;\r\n        this.itemDao = itemDao;\r\n        this.customerDao = customerDao;\r\n    }\r\n    \r\n    //clientAppUserId=100&itemsIds[0]=1&itemsIds[1]=2&itemsIds[2]=3\r\n    @Post(\"app/{key}/addSale\")\r\n    public void addSale(String key, Long clientAppUserId, List<Long> itemsIds) {\r\n        ClientApp app = appDao.findByAccessKey(key);\r\n        GroupedItems groupedItems = itemDao.findItems(itemsIds, app);\r\n        \r\n        ArrayList<Item> newItems = groupedItems.addRemaining(itemsIds, app);\r\n        \r\n        for (Item item : newItems) {\r\n            itemDao.save(item);\r\n        }\r\n        \r\n        Customer customer = customerDao.find(app, clientAppUserId);\r\n        List<Item> allItems = groupedItems.getItems();\r\n        if (customer == null) {\r\n            customer = new Customer(new ArrayList<Item>(), clientAppUserId, app);\r\n        }\r\n        for (Item item : allItems) {\r\n            customer.add(item);\r\n        }\r\n        customerDao.save(customer);\r\n        result.use(Results.json()).from(true).serialize();\r\n    }\r\n\r\n}\r\n',1990,246),(247,'package br.ime.usp.commendans.controller;\r\n\r\nimport java.util.ArrayList;\r\nimport java.util.List;\r\n\r\nimport br.com.caelum.vraptor.Get;\r\nimport br.com.caelum.vraptor.Resource;\r\nimport br.com.caelum.vraptor.Result;\r\nimport br.com.caelum.vraptor.view.Results;\r\nimport br.ime.usp.commendans.dao.ClientAppDao;\r\nimport br.ime.usp.commendans.dao.ItemDao;\r\nimport br.ime.usp.commendans.infra.TupleJsonSerializer;\r\nimport br.ime.usp.commendans.model.ClientApp;\r\nimport br.ime.usp.commendans.model.Item;\r\nimport br.ime.usp.commendans.recommender.GeneralRecommender;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.ItemVector;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.Tuple;\r\n\r\n@Resource\r\npublic class ItemToItemController {\r\n    private final ItemDao itemDao;\r\n    private final Result result;\r\n    private final TupleJsonSerializer serializer;\r\n    private final GeneralRecommender recommender;\r\n    private final ClientAppDao appDao;\r\n\r\n    public ItemToItemController(GeneralRecommender recommender, \r\n            ItemDao itemDao, ClientAppDao appDao, Result result, \r\n            TupleJsonSerializer serializer) {\r\n        this.recommender = recommender;\r\n        this.itemDao = itemDao;\r\n        this.appDao = appDao;\r\n        this.result = result;\r\n        this.serializer = serializer;\r\n    }\r\n    \r\n    @Get(\"/recommend/item/{appItemId}\")\r\n    public void recommend(Long appItemId, String accessKey) {\r\n        if (validKey(accessKey)) {\r\n            ClientApp app = appDao.findByAccessKey(accessKey);\r\n            Item item = itemDao.findByAppItemId(appItemId, app);\r\n            ItemVector recommended = recommender.recommendedItemsFor(item, accessKey);\r\n            serializeResult(recommended);\r\n        }\r\n    }\r\n    \r\n\r\n    @Get(\"/recommend/items/\")\r\n    public void recommend(List<Long> itemsIds, String accessKey) {\r\n        if (validKey(accessKey)) {\r\n            ClientApp app = appDao.findByAccessKey(accessKey);\r\n            List<Item> items = itemDao.findItems(itemsIds, app).getItems();\r\n            ItemVector recommended = recommender.recommendedItemsFor(items, accessKey);\r\n            serializeResult(recommended);\r\n        }\r\n    }\r\n\r\n    private boolean validKey(String accessKey) {\r\n        ClientApp app = appDao.findByAccessKey(accessKey);\r\n        if (app == null) {\r\n            result.notFound();\r\n            return false;\r\n        }\r\n        return true;\r\n    }\r\n\r\n    private void serializeResult(ItemVector recommended) {\r\n        List<Tuple> tuples = recommended == null ? new ArrayList<Tuple>() : recommended.getTuples();\r\n        result.use(Results.http())\r\n            .addHeader(\"content-type\", \"application/json\")\r\n            .body(serializer.toJson(tuples));\r\n    }\r\n}\r\n',2722,247),(248,'',0,248),(249,'package br.ime.usp.commendans.dao;\r\n\r\nimport java.util.List;\r\n\r\nimport org.hibernate.Query;\r\nimport org.hibernate.Session;\r\n\r\nimport br.com.caelum.vraptor.ioc.Component;\r\nimport br.ime.usp.commendans.model.ClientApp;\r\n\r\n@Component\r\npublic class ClientAppDao {\r\n    \r\n    private final Session session;\r\n\r\n    public ClientAppDao(Session session) {\r\n        this.session = session;\r\n    }\r\n    \r\n    @SuppressWarnings(\"unchecked\")\r\n    public List<ClientApp> list() {\r\n        return session.createCriteria(ClientApp.class).list();\r\n    }\r\n\r\n    public ClientApp findByAccessKey(String key) {\r\n        Query query = session.createQuery(\"select app from ClientApp app where app.accessKey=:key\");\r\n        query.setParameter(\"key\", key);\r\n        return (ClientApp) query.uniqueResult();\r\n    }\r\n\r\n\r\n}\r\n',800,249),(250,'package br.ime.usp.commendans.dao;\r\n\r\nimport java.util.List;\r\n\r\nimport org.hibernate.Query;\r\nimport org.hibernate.Session;\r\n\r\nimport br.com.caelum.vraptor.ioc.Component;\r\nimport br.com.caelum.vraptor.ioc.PrototypeScoped;\r\nimport br.ime.usp.commendans.model.ClientApp;\r\nimport br.ime.usp.commendans.model.Customer;\r\n\r\n@Component @PrototypeScoped\r\npublic class CustomerDao {\r\n    private final Session session;\r\n\r\n    public CustomerDao(Session session) {\r\n        this.session = session;\r\n    }\r\n    \r\n    @SuppressWarnings(\"unchecked\")\r\n    public List<Customer> list() {\r\n        return session.createCriteria(Customer.class).list();\r\n    }\r\n\r\n    @SuppressWarnings(\"unchecked\")\r\n    public List<Customer> findCustomersOf(ClientApp app) {\r\n        Query query = session.createQuery(\"select customer from Customer customer \" +\r\n        		\"join fetch customer.items \" +\r\n        		\"where customer.app.name like :appName\");\r\n        query.setParameter(\"appName\", app.getName());\r\n        return query.list();\r\n    }\r\n\r\n    public void save(Customer customer) {\r\n        session.saveOrUpdate(customer);\r\n    }\r\n\r\n    public Customer find(ClientApp app, Long customerId) {\r\n        Query query = session.createQuery(\"select customer from Customer customer \" +\r\n                \"where customer.app.id like :appId \" +\r\n                \"and customer.clientAppCustomerId = :customerId\");\r\n        query.setParameter(\"appId\", app.getId());\r\n        query.setParameter(\"customerId\", customerId);\r\n        return (Customer) query.uniqueResult();\r\n    }\r\n}\r\n',1546,250),(251,'package br.ime.usp.commendans.dao;\r\n\r\nimport java.util.List;\r\n\r\nimport org.hibernate.Query;\r\nimport org.hibernate.Session;\r\n\r\nimport br.com.caelum.vraptor.ioc.Component;\r\nimport br.ime.usp.commendans.model.ClientApp;\r\nimport br.ime.usp.commendans.model.GroupedItems;\r\nimport br.ime.usp.commendans.model.Item;\r\n\r\n@Component\r\npublic class ItemDao {\r\n    \r\n    private final Session session;\r\n\r\n    public ItemDao(Session session) {\r\n        this.session = session;\r\n    }\r\n\r\n    public Item findByAppItemId(Long appItemId, ClientApp app) {\r\n        Query query = session.createQuery(\"select item from Item item where item.appItemId = :id and item.app.id = :appId\");\r\n        query.setLong(\"id\", appItemId);\r\n        query.setLong(\"appId\", app.getId());\r\n        return (Item) query.uniqueResult();\r\n    }\r\n\r\n    @SuppressWarnings(\"unchecked\")\r\n    public GroupedItems findItems(List<Long> itemsIds, ClientApp app) {\r\n        List<Item> items = session\r\n                .createQuery(\"select item from Item item where item.appItemId in :ids and item.app.id = :appId\")\r\n                .setParameterList(\"ids\", itemsIds)\r\n                .setParameter(\"appId\", app.getId())\r\n                .list();\r\n        return new GroupedItems(items);\r\n    }\r\n\r\n    public void save(Item item) {\r\n        session.save(item);\r\n    }\r\n\r\n}\r\n',1322,251),(252,'package br.ime.usp.commendans.infra;\r\n\r\nimport java.io.BufferedReader;\r\nimport java.io.IOException;\r\nimport java.io.InputStream;\r\nimport java.io.InputStreamReader;\r\nimport java.util.ArrayList;\r\nimport java.util.Arrays;\r\nimport java.util.Collection;\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\n\r\nimport org.apache.log4j.Logger;\r\nimport org.hibernate.Session;\r\nimport org.hibernate.SessionFactory;\r\nimport org.hibernate.cfg.Configuration;\r\n\r\nimport br.ime.usp.commendans.model.ClientApp;\r\nimport br.ime.usp.commendans.model.Customer;\r\nimport br.ime.usp.commendans.model.Item;\r\n\r\npublic class DataImporter {\r\n    \r\n    private final Session session;\r\n    private static Logger logger = Logger.getLogger(DataImporter.class);\r\n\r\n    public DataImporter(Session session) {\r\n        this.session = session;\r\n    }\r\n\r\n    public static void main(String[] args) throws IOException {\r\n        SessionFactory sf = new Configuration().configure(\"/hibernate.cfg.xml\").buildSessionFactory();\r\n        Session session = sf.openSession();\r\n        DataImporter dataImporter = new DataImporter(session);\r\n        dataImporter.importData(\"/orders.csv\");\r\n    }\r\n\r\n    public void importData(String file) {\r\n        HashMap<Long, Customer> users = new HashMap<Long, Customer>();\r\n        HashMap<Long, Item> items = new HashMap<Long, Item>();\r\n        InputStream resourceAsStream = getClass().getResourceAsStream(file);\r\n        BufferedReader reader = new BufferedReader(new InputStreamReader(resourceAsStream));\r\n        String line = safeReadLine(reader);\r\n        ClientApp cdc = new ClientApp(\"Casa do Cdigo\", \"123\");\r\n        session.save(cdc);\r\n        \r\n        while (line != null) {\r\n            logger.debug(line);\r\n            List<String> row = Arrays.asList(line.split(\",\"));\r\n            long id = Long.parseLong(row.get(0));\r\n            long itemId = Long.parseLong(row.get(1).charAt(7) + \"\");\r\n            Customer user = users.get(id);\r\n            if (user == null) {\r\n                user = new Customer(new ArrayList<Item>(), id, cdc);\r\n            }\r\n            Item item = items.get(itemId);\r\n            if (item == null) {\r\n                item = new Item(itemId, cdc);\r\n            }\r\n            user.add(item);\r\n            line = safeReadLine(reader);\r\n            users.put(id, user);\r\n            items.put(itemId, item);\r\n        }\r\n        \r\n        logger.debug(\"persisting\");\r\n        session.getTransaction().begin();\r\n        for (Item item : items.values()) {\r\n            session.save(item);\r\n        }\r\n        \r\n        Collection<Customer> allUsers = users.values();\r\n        for (Customer user : allUsers) {\r\n            session.save(user);\r\n        }\r\n        session.getTransaction().commit();\r\n        logger.debug(\"finished persisting\");\r\n    }\r\n\r\n    private String safeReadLine(BufferedReader reader) {\r\n        try {\r\n            return reader.readLine();\r\n        } catch (IOException e) {\r\n            throw new RuntimeException(\"could not read file\", e);\r\n        }\r\n    }\r\n\r\n}\r\n',3025,252),(253,'',0,253),(254,'package br.ime.usp.commendans.model;\r\n\r\nimport java.util.List;\r\n\r\nimport javax.persistence.Entity;\r\nimport javax.persistence.GeneratedValue;\r\nimport javax.persistence.Id;\r\nimport javax.persistence.Transient;\r\n\r\nimport br.ime.usp.commendans.recommender.itemtoitem.ItemVector;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.SingleAppRecommender;\r\n\r\n@Entity\r\npublic class ClientApp {\r\n\r\n\r\n    @Id\r\n    @GeneratedValue\r\n    private Long id;\r\n\r\n    private String name;\r\n\r\n    private String accessKey;\r\n\r\n    @Transient\r\n    private SingleAppRecommender recommender;\r\n\r\n    @Deprecated\r\n    protected ClientApp() {\r\n    }\r\n\r\n    public ClientApp(String name, String accessKey) {\r\n        this.name = name;\r\n        this.accessKey = accessKey;\r\n    }\r\n\r\n    public String getName() {\r\n        return name;\r\n    }\r\n\r\n    public void use(SingleAppRecommender recommender) {\r\n        this.recommender = recommender;\r\n    }\r\n\r\n    public Long getId() {\r\n        return id;\r\n    }\r\n\r\n    public ItemVector recommendedItemsFor(Item item) {\r\n        return recommender.recommendedItemsFor(item);\r\n    }\r\n\r\n    public ItemVector recommendedItemsFor(List<Item> items) {\r\n        return recommender.recommendedItemsFor(items);\r\n    }\r\n\r\n    public String getAccessKey() {\r\n        return accessKey;\r\n    }\r\n\r\n    @Override\r\n    public String toString() {\r\n        return \"Application [id=\" + id + \", name=\" + name + \", accessKey=\"\r\n                + accessKey + \"]\";\r\n    }\r\n    @Override\r\n    public int hashCode() {\r\n        final int prime = 31;\r\n        int result = 1;\r\n        result = prime * result + ((id == null) ? 0 : id.hashCode());\r\n        return result;\r\n    }\r\n    \r\n    @Override\r\n    public boolean equals(Object obj) {\r\n        if (this == obj)\r\n            return true;\r\n        if (obj == null)\r\n            return false;\r\n        if (getClass() != obj.getClass())\r\n            return false;\r\n        ClientApp other = (ClientApp) obj;\r\n        if (id == null) {\r\n            if (other.id != null)\r\n                return false;\r\n        } else if (!id.equals(other.id))\r\n            return false;\r\n        return true;\r\n    }\r\n}\r\n',2143,254),(255,'package br.ime.usp.commendans.model;\r\n\r\nimport java.util.List;\r\n\r\nimport javax.persistence.Entity;\r\nimport javax.persistence.GeneratedValue;\r\nimport javax.persistence.Id;\r\nimport javax.persistence.ManyToMany;\r\nimport javax.persistence.ManyToOne;\r\n\r\n@Entity\r\npublic class Customer {\r\n    \r\n    @Id @GeneratedValue\r\n    private Long id;\r\n    \r\n    private Long clientAppCustomerId;\r\n\r\n    @ManyToMany\r\n    private List<Item> items;\r\n    \r\n    @ManyToOne\r\n    private ClientApp app;\r\n    \r\n    @Deprecated\r\n    public Customer() {\r\n    }\r\n\r\n    public Customer(List<Item> items, Long clientAppCustomerId, ClientApp app) {\r\n        this.items = items;\r\n        this.clientAppCustomerId = clientAppCustomerId;\r\n        this.app = app;\r\n    }\r\n\r\n    public List<Item> itemsBought() {\r\n        return items;\r\n    }\r\n    \r\n    @Override\r\n    public String toString() {\r\n        return \"User \" + clientAppCustomerId + \" from \" + app;\r\n    }\r\n\r\n    public void add(Item item) {\r\n        items.add(item);\r\n    }\r\n    \r\n    public ClientApp getApp() {\r\n        return app;\r\n    }\r\n    \r\n    public Long getClientAppCustomerId() {\r\n        return clientAppCustomerId;\r\n    }\r\n    \r\n}\r\n',1172,255),(256,'package br.ime.usp.commendans.model;\r\n\r\nimport java.util.ArrayList;\r\nimport java.util.List;\r\n\r\npublic class GroupedItems {\r\n\r\n    private final List<Item> items;\r\n\r\n    public GroupedItems(List<Item> items) {\r\n        this.items = new ArrayList(items);\r\n    }\r\n\r\n    public ArrayList<Item> addRemaining(List<Long> ids, ClientApp app) {\r\n        ArrayList<Item> added = new ArrayList<Item>();\r\n        for (Long id : ids) {\r\n            Item item = new Item(id, app);\r\n            if (!items.contains(item)) {\r\n                items.add(item);\r\n                added.add(item);\r\n            }\r\n        }\r\n        return added;\r\n\r\n    }\r\n\r\n    public List<Item> getItems() {\r\n        return items;\r\n    }\r\n\r\n}\r\n',709,256),(257,'package br.ime.usp.commendans.model;\r\n\r\nimport static org.junit.Assert.assertEquals;\r\nimport static org.junit.Assert.assertTrue;\r\n\r\nimport java.util.Arrays;\r\nimport java.util.List;\r\n\r\nimport org.junit.Test;\r\n\r\npublic class GroupedItemsTest {\r\n\r\n    @Test\r\n    public void shouldAddReaminingItems() {\r\n        Item item1 = new Item(1l, null);\r\n        Item item2 = new Item(2l, null);\r\n        GroupedItems groupedItems = new GroupedItems(Arrays.asList(item1, item2));\r\n        List<Long> ids = Arrays.asList(1l, 2l, 3l, 4l);\r\n        groupedItems.addRemaining(ids, null);\r\n        \r\n        List<Item> items = groupedItems.getItems();\r\n        \r\n        assertEquals(4, items.size());\r\n        assertTrue(items.contains(item1));\r\n        assertTrue(items.contains(item2));\r\n        assertTrue(items.contains(new Item(3l, null)));\r\n        assertTrue(items.contains(new Item(4l, null)));\r\n    }\r\n\r\n}\r\n',900,257),(258,'package br.ime.usp.commendans.model;\r\n\r\nimport javax.persistence.Entity;\r\nimport javax.persistence.GeneratedValue;\r\nimport javax.persistence.Id;\r\nimport javax.persistence.ManyToOne;\r\n\r\n@Entity\r\npublic class Item {\r\n    @Id @GeneratedValue\r\n    private Long id;\r\n    \r\n    private Long appItemId;\r\n    \r\n    @ManyToOne\r\n    private ClientApp app;\r\n\r\n    @Deprecated\r\n    protected Item() {\r\n    }\r\n    \r\n    public Item(Long appItemId, ClientApp app) {\r\n        this.appItemId = appItemId;\r\n        this.app = app;\r\n    }\r\n    \r\n    public Long getAppItemId() {\r\n        return appItemId;\r\n    }\r\n    \r\n    public ClientApp getApp() {\r\n        return app;\r\n    }\r\n    \r\n    @Override\r\n    public String toString() {\r\n        return \"Item \" + id;\r\n    }\r\n\r\n    @Override\r\n    public int hashCode() {\r\n        final int prime = 31;\r\n        int result = 1;\r\n        result = prime * result + ((app == null) ? 0 : app.hashCode());\r\n        result = prime * result\r\n                + ((appItemId == null) ? 0 : appItemId.hashCode());\r\n        return result;\r\n    }\r\n\r\n    @Override\r\n    public boolean equals(Object obj) {\r\n        if (this == obj)\r\n            return true;\r\n        if (obj == null)\r\n            return false;\r\n        if (getClass() != obj.getClass())\r\n            return false;\r\n        Item other = (Item) obj;\r\n        if (app == null) {\r\n            if (other.app != null)\r\n                return false;\r\n        } else if (!app.equals(other.app))\r\n            return false;\r\n        if (appItemId == null) {\r\n            if (other.appItemId != null)\r\n                return false;\r\n        } else if (!appItemId.equals(other.appItemId))\r\n            return false;\r\n        return true;\r\n    }\r\n}\r\n',1716,258),(259,'package br.ime.usp.commendans.recommender;\r\n\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\nimport java.util.Map;\r\n\r\nimport br.ime.usp.commendans.model.ClientApp;\r\nimport br.ime.usp.commendans.model.Item;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.ItemVector;\r\n\r\npublic class GeneralRecommender {\r\n\r\n    private Map<String, ClientApp> appsMap;\r\n\r\n    public GeneralRecommender(List<ClientApp> apps) {\r\n        appsMap = new HashMap<String, ClientApp>();\r\n        for (ClientApp app : apps) {\r\n            appsMap.put(app.getAccessKey(), app);\r\n        }\r\n    }\r\n\r\n    public ItemVector recommendedItemsFor(Item item, String accessKey) {\r\n        ClientApp application = appsMap.get(accessKey);\r\n        return application.recommendedItemsFor(item);\r\n    }\r\n\r\n    public ItemVector recommendedItemsFor(List<Item> items, String accessKey) {\r\n        ClientApp application = appsMap.get(accessKey);\r\n        return application.recommendedItemsFor(items);\r\n    }\r\n\r\n}\r\n',980,259),(260,'package br.ime.usp.commendans.recommender;\r\n\r\nimport java.util.List;\r\n\r\nimport javax.annotation.PostConstruct;\r\n\r\nimport org.hibernate.Session;\r\nimport org.hibernate.SessionFactory;\r\n\r\nimport br.com.caelum.vraptor.ioc.ApplicationScoped;\r\nimport br.com.caelum.vraptor.ioc.Component;\r\nimport br.com.caelum.vraptor.ioc.ComponentFactory;\r\nimport br.ime.usp.commendans.dao.ClientAppDao;\r\nimport br.ime.usp.commendans.dao.CustomerDao;\r\nimport br.ime.usp.commendans.model.ClientApp;\r\nimport br.ime.usp.commendans.model.Customer;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.SingleAppRecommender;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.SingleAppRecommenderFactory;\r\n\r\n@Component @ApplicationScoped\r\npublic class RecommenderCreator implements ComponentFactory<GeneralRecommender> {\r\n    private final CustomerDao customerDao;\r\n    private final SingleAppRecommenderFactory factory;\r\n    private GeneralRecommender recommender;\r\n    private final ClientAppDao appDao;\r\n\r\n    public RecommenderCreator(SingleAppRecommenderFactory factory, SessionFactory sf) {\r\n        Session session = sf.openSession();\r\n        this.customerDao = new CustomerDao(session);\r\n        this.appDao = new ClientAppDao(session);\r\n        this.factory = factory;\r\n    }\r\n\r\n    @Override\r\n    public GeneralRecommender getInstance() {\r\n        return recommender;\r\n    }\r\n    \r\n    @PostConstruct\r\n    public void create() {\r\n        List<ClientApp> apps = appDao.list();\r\n        for (ClientApp app : apps) {\r\n            List<Customer> customers = customerDao.findCustomersOf(app);\r\n            SingleAppRecommender recommender = buildSingleAppRecommender(customers);\r\n            app.use(recommender);\r\n        }\r\n        this.recommender = new GeneralRecommender(apps);\r\n    }\r\n    \r\n    private SingleAppRecommender buildSingleAppRecommender(List<Customer> users) {\r\n        factory.clean();\r\n        for (Customer user : users) {\r\n            factory.addUser(user);\r\n        }\r\n        return factory.build();\r\n    }\r\n}\r\n',2016,260),(261,'<?xml version=\'1.0\' encoding=\'utf-8\'?>\r\n<!DOCTYPE hibernate-configuration PUBLIC\r\n        \"-//Hibernate/Hibernate Configuration DTD//EN\"\r\n        \"http://www.hibernate.org/dtd/hibernate-configuration-3.0.dtd\">\r\n<hibernate-configuration>\r\n    <session-factory>\r\n    \r\n    	<!-- connection pool configs -->\r\n<!-- 	    <property name=\"hibernate.c3p0.acquire_increment\">1</property>  -->\r\n<!-- 		<property name=\"hibernate.c3p0.idle_test_period\">100</property> seconds  -->\r\n<!-- 		<property name=\"hibernate.c3p0.max_size\">15</property>  -->\r\n<!-- 		<property name=\"hibernate.c3p0.min_size\">1</property>  -->\r\n<!-- 		<property name=\"hibernate.c3p0.timeout\">600</property> -->\r\n    \r\n        <property name=\"show_sql\">false</property>\r\n        <property name=\"format_sql\">false</property>\r\n        <property name=\"hibernate.hbm2ddl.auto\">update</property>\r\n\r\n        <mapping class=\"br.ime.usp.commendans.model.Item\" />\r\n        <mapping class=\"br.ime.usp.commendans.model.Customer\" />\r\n        <mapping class=\"br.ime.usp.commendans.model.ClientApp\" />\r\n\r\n    </session-factory>\r\n</hibernate-configuration>\r\n',1102,261),(262,'<?xml version=\'1.0\' encoding=\'utf-8\'?>\r\n<!DOCTYPE hibernate-configuration PUBLIC\r\n        \"-//Hibernate/Hibernate Configuration DTD//EN\"\r\n        \"http://www.hibernate.org/dtd/hibernate-configuration-3.0.dtd\">\r\n<hibernate-configuration>\r\n    <session-factory>\r\n    \r\n    	<!-- connection pool configs -->\r\n	    <property name=\"c3p0.acquire_increment\">1</property> \r\n		<property name=\"c3p0.idle_test_period\">100</property> <!-- seconds --> \r\n		<property name=\"c3p0.max_size\">15</property> \r\n		<property name=\"c3p0.min_size\">1</property> \r\n		<property name=\"c3p0.timeout\">600</property>\r\n    \r\n        <property name=\"connection.url\">jdbc:mysql://localhost/commendans</property>\r\n        <property name=\"connection.driver_class\">com.mysql.jdbc.Driver</property>\r\n        <property name=\"dialect\">org.hibernate.dialect.MySQL5InnoDBDialect</property>\r\n        <property name=\"connection.username\">root</property>\r\n        <property name=\"connection.password\"></property>\r\n        <property name=\"hibernate.hbm2ddl.auto\">create-drop</property>\r\n        <property name=\"show_sql\">true</property>\r\n        <property name=\"format_sql\">false</property>\r\n        <property name=\"hibernate.generate_statistics\">true</property>\r\n\r\n        <mapping class=\"br.ime.usp.commendans.model.Item\" />\r\n        <mapping class=\"br.ime.usp.commendans.model.Customer\" />\r\n        <mapping class=\"br.ime.usp.commendans.model.ClientApp\" />\r\n\r\n    </session-factory>\r\n</hibernate-configuration>\r\n',1468,262),(263,'',0,263),(264,'package br.ime.usp.commendans.dao;\r\n\r\nimport static org.junit.Assert.assertEquals;\r\n\r\nimport java.util.List;\r\n\r\nimport org.junit.After;\r\nimport org.junit.Before;\r\nimport org.junit.Test;\r\n\r\nimport br.ime.usp.commendans.model.ClientApp;\r\n\r\npublic class ClientAppDaoTest extends DaoTest {\r\n    private static ClientAppDao applicationDao;\r\n\r\n    @Before\r\n    public void setUp() {\r\n        applicationDao = new ClientAppDao(session);\r\n        session.beginTransaction();\r\n    }\r\n    \r\n    @After\r\n    public void tearDown() {\r\n        session.getTransaction().rollback();\r\n        session.clear();\r\n    }\r\n    \r\n    @Test\r\n    public void shouldListApps() throws Exception {\r\n        session.save(new ClientApp(\"cdc\", \"123\"));\r\n        session.save(new ClientApp(\"caelum\", \"1234\"));\r\n        List<ClientApp> apps = applicationDao.list();\r\n        assertEquals(2, apps.size());\r\n    }\r\n    \r\n    @Test\r\n    public void shouldFindAppByAccessKey() throws Exception {\r\n        session.save(new ClientApp(\"cdc\", \"123\"));\r\n        session.save(new ClientApp(\"caelum\", \"1234\"));\r\n        ClientApp app = applicationDao.findByAccessKey(\"1234\");\r\n        assertEquals(\"caelum\", app.getName());\r\n    }\r\n\r\n}\r\n',1194,264),(265,'package br.ime.usp.commendans.dao;\r\n\r\nimport static org.junit.Assert.assertEquals;\r\n\r\nimport java.util.Arrays;\r\nimport java.util.List;\r\n\r\nimport org.junit.After;\r\nimport org.junit.Before;\r\nimport org.junit.Test;\r\n\r\nimport br.ime.usp.commendans.model.ClientApp;\r\nimport br.ime.usp.commendans.model.Customer;\r\nimport br.ime.usp.commendans.model.Item;\r\n\r\npublic class CustomerDaoTest extends DaoTest {\r\n\r\n    private static CustomerDao userDao;\r\n    private static ClientApp app;\r\n\r\n    @Before\r\n    public void setUp() {\r\n        userDao = new CustomerDao(session);\r\n        app = new ClientApp(\"cdc\", \"123\");\r\n        session.beginTransaction();\r\n        session.save(app);\r\n    }\r\n    \r\n    @After\r\n    public void tearDown() {\r\n        session.getTransaction().rollback();\r\n        session.clear();\r\n    }\r\n\r\n    @Test\r\n    public void shouldFindUsers() {\r\n        Item item = new Item(1l, app);\r\n        session.save(item);\r\n        List<Item> items = Arrays.asList(item);\r\n        session.save(new Customer(items, 1l, app));\r\n        session.save(new Customer(items, 2l, app));\r\n        session.save(new Customer(items, 3l, app));\r\n        List<Customer> users = userDao.list();\r\n        assertEquals(3, users.size());\r\n    }\r\n\r\n    @Test\r\n    public void shouldFindUsersOfSpecificApp() throws Exception {\r\n        saveCustomers();    \r\n\r\n        List<Customer> customers = userDao.findCustomersOf(app);\r\n        assertEquals(3, customers.size());\r\n        assertEquals(\"cdc\", customers.get(0).getApp().getName());\r\n        assertEquals(\"cdc\", customers.get(1).getApp().getName());\r\n        assertEquals(\"cdc\", customers.get(2).getApp().getName());\r\n    }\r\n    \r\n    @Test\r\n    public void shouldFindCustomerWithIdAndApp() throws Exception {\r\n        saveCustomers();    \r\n        \r\n        Customer c = userDao.find(app, 1l);\r\n        assertEquals(\"cdc\", c.getApp().getName());\r\n        assertEquals(1l, c.getClientAppCustomerId().longValue());\r\n    }\r\n\r\n    private void saveCustomers() {\r\n        Item item = new Item(1l, app);\r\n        session.save(item);\r\n        List<Item> items = Arrays.asList(item);\r\n        session.save(new Customer(items, 1l, app));\r\n        session.save(new Customer(items, 2l, app));\r\n        session.save(new Customer(items, 3l, app));\r\n        ClientApp otherApp = new ClientApp(\"gnarus\", \"123\");\r\n        item = new Item(2l, otherApp);\r\n        items = Arrays.asList(item);\r\n        session.save(otherApp);\r\n        session.save(item);\r\n        session.save(new Customer(items, 1l, otherApp));\r\n        session.save(new Customer(items, 2l, otherApp));\r\n        session.save(new Customer(items, 3l, otherApp));\r\n    }\r\n\r\n}\r\n',2660,265),(266,'package br.ime.usp.commendans.dao;\r\n\r\nimport static org.junit.Assert.assertEquals;\r\n\r\nimport org.junit.After;\r\nimport org.junit.Before;\r\nimport org.junit.Test;\r\n\r\nimport br.ime.usp.commendans.model.ClientApp;\r\nimport br.ime.usp.commendans.model.Item;\r\n\r\npublic class ItemDaoTest extends DaoTest {\r\n    \r\n    private ItemDao itemDao;\r\n\r\n    @Before\r\n    public void setUp() {\r\n        itemDao = new ItemDao(session);\r\n        session.beginTransaction();\r\n    }\r\n    \r\n    @After\r\n    public void tearDown() {\r\n        session.getTransaction().rollback();\r\n        session.clear();\r\n    }\r\n    \r\n    @Test\r\n    public void shouldFindByAppItemId() throws Exception {\r\n        ClientApp app = new ClientApp(\"cdc\", \"123\");\r\n        ClientApp app2 = new ClientApp(\"caelum\", \"1234\");\r\n        session.save(app);\r\n        session.save(app2);\r\n        session.save(new Item(11l, app));\r\n        session.save(new Item(11l, app2));\r\n        Item item = itemDao.findByAppItemId(11l, app);\r\n        assertEquals(11l, item.getAppItemId().longValue());\r\n        assertEquals(\"cdc\", item.getApp().getName());\r\n    }\r\n\r\n}\r\n',1106,266),(267,'package br.ime.usp.commendans.infra;\r\n\r\nimport static org.junit.Assert.assertEquals;\r\n\r\nimport java.util.Arrays;\r\n\r\nimport org.junit.Test;\r\n\r\nimport br.ime.usp.commendans.model.ClientApp;\r\nimport br.ime.usp.commendans.model.Item;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.Tuple;\r\n\r\npublic class TupleJsonSerializerTest {\r\n\r\n    @Test\r\n    public void shouldSerializeATuple() {\r\n        TupleJsonSerializer serializer = new TupleJsonSerializer();\r\n        ClientApp cdc = new ClientApp(\"Casa do Cdigo\", \"123\");\r\n        String json = serializer.toJson(new Tuple(new Item(1l, cdc), 10.0));\r\n        assertEquals(\"{\\\"item\\\":{\\\"appItemId\\\":1},\\\"value\\\":10.0}\", json);\r\n    }\r\n    \r\n    @Test\r\n    public void shouldSerializeATupleList() {\r\n        TupleJsonSerializer serializer = new TupleJsonSerializer();\r\n        ClientApp cdc = new ClientApp(\"Casa do Cdigo\", \"123\");\r\n        String json = serializer.toJson(Arrays.asList(new Tuple(new Item(1l, cdc), 10.0)));\r\n        assertEquals(\"[{\\\"item\\\":{\\\"appItemId\\\":1},\\\"value\\\":10.0}]\", json);\r\n    }\r\n\r\n}\r\n',1065,267),(268,'package br.ime.usp.commendans.recommender.itemtoitem;\r\n\r\nimport static org.junit.Assert.assertEquals;\r\n\r\nimport java.util.ArrayList;\r\nimport java.util.HashSet;\r\nimport java.util.List;\r\n\r\nimport org.junit.Test;\r\n\r\nimport br.ime.usp.commendans.model.ClientApp;\r\nimport br.ime.usp.commendans.model.Item;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.ItemVector;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.Tuple;\r\n\r\npublic class ItemVectorTest {\r\n\r\n    @Test\r\n    public void shouldMergeTuples() {\r\n        ClientApp cdc = new ClientApp(\"Casa do Cdigo\", \"123\");\r\n        Item item1 = new Item(1l, cdc);\r\n        Item item2 = new Item(2l, cdc);\r\n        Item item3 = new Item(3l, cdc);\r\n        Item item4 = new Item(4l, cdc);\r\n        List<Tuple> tuples = asList(new Tuple(item1, 11.0), new Tuple(item2, 8.0), new Tuple(item3, 2.0));\r\n        ItemVector itemVector1 = new ItemVector(new HashSet<Tuple>(tuples));\r\n        tuples = asList(new Tuple(item4, 10.0), new Tuple(item3, 7.0));\r\n        ItemVector itemVector2 = new ItemVector(new HashSet<Tuple>(tuples));\r\n        \r\n        ItemVector merge = itemVector1.merge(itemVector2);\r\n        List<Tuple> expected = asList(new Tuple(item1, 11.0), new Tuple(item4, 10.0), new Tuple(item3, 9.0), new Tuple(item2, 8.0));\r\n        assertEquals(expected, merge.getTuples());\r\n        \r\n    }\r\n\r\n    private List<Tuple> asList(Tuple ... tuples) {\r\n        ArrayList<Tuple> tuplesList = new ArrayList<Tuple>();\r\n        for (int i = 0; i < tuples.length; i++) {\r\n            tuplesList.add(tuples[i]);\r\n        }\r\n        return tuplesList;\r\n    }\r\n\r\n}\r\n',1610,268),(269,'package br.ime.usp.commendans.recommender.itemtoitem;\r\n\r\nimport static org.junit.Assert.assertEquals;\r\n\r\nimport java.util.Arrays;\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\n\r\nimport org.junit.Test;\r\n\r\nimport br.ime.usp.commendans.model.ClientApp;\r\nimport br.ime.usp.commendans.model.Customer;\r\nimport br.ime.usp.commendans.model.Item;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.ItemToItemsAssociation;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.SingleAppRecommender;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.SingleAppRecommenderFactory;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.Tuple;\r\n\r\npublic class SingleAppRecommenderFactoryTest {\r\n\r\n    @Test\r\n    public void shouldBuildRecommenderFromCollectiveIntelligenceInActionExample() {\r\n        SingleAppRecommenderFactory factory = new SingleAppRecommenderFactory();\r\n        ClientApp cdc = new ClientApp(\"casadocodigo\", \"1234\");\r\n        Item item1 = new Item(1l, cdc);\r\n        Item item2 = new Item(2l, cdc);\r\n        Item item3 = new Item(3l, cdc);\r\n        Item item4 = new Item(4l, cdc);\r\n        Customer user1 = new Customer(Arrays.asList(item1, item3), 1l, cdc);\r\n        Customer user2 = new Customer(Arrays.asList(item2, item4), 2l, cdc);\r\n        Customer user3 = new Customer(Arrays.asList(item1, item2, item3), 3l, cdc);\r\n        Customer user4 = new Customer(Arrays.asList(item3, item4), 4l, cdc);\r\n        factory.addUser(user1);\r\n        factory.addUser(user2);\r\n        factory.addUser(user3);\r\n        factory.addUser(user4);\r\n        \r\n        SingleAppRecommender recommender = factory.build();\r\n        HashMap<Item, ItemToItemsAssociation> associations = factory.getAssociations();\r\n        \r\n        List<Tuple> recommendedItems = recommender.recommendedItemsFor(item1).getTuples();\r\n        ItemToItemsAssociation association = associations.get(item1);\r\n        assertEquals(0.0, association.associationValue(item1), 0.001);\r\n        assertEquals(1.0, association.associationValue(item2), 0.001);\r\n        assertEquals(2.0, association.associationValue(item3), 0.001);\r\n        assertEquals(0.0, association.associationValue(item4), 0.001);\r\n        \r\n        assertEquals(2, recommendedItems.size());\r\n        assertEquals(item3, recommendedItems.get(0).getItem());\r\n        assertEquals(2.0, recommendedItems.get(0).getValue().doubleValue(), 0.001);\r\n        assertEquals(item2, recommendedItems.get(1).getItem());\r\n        assertEquals(1.0, recommendedItems.get(1).getValue().doubleValue(), 0.001);\r\n        \r\n        recommendedItems = recommender.recommendedItemsFor(item2).getTuples();\r\n        association = associations.get(item2);\r\n        assertEquals(1.0, association.associationValue(item1), 0.001);\r\n        assertEquals(0.0, association.associationValue(item2), 0.001);\r\n        assertEquals(1.0, association.associationValue(item3), 0.001);\r\n        assertEquals(1.0, association.associationValue(item4), 0.001);\r\n        \r\n        assertEquals(3, recommendedItems.size());\r\n        assertEquals(1.0, recommendedItems.get(0).getValue().doubleValue(), 0.001);\r\n        assertEquals(1.0, recommendedItems.get(1).getValue().doubleValue(), 0.001);\r\n        assertEquals(1.0, recommendedItems.get(2).getValue().doubleValue(), 0.001);\r\n        \r\n        recommendedItems = recommender.recommendedItemsFor(item3).getTuples();\r\n        association = associations.get(item3);\r\n        assertEquals(2.0, association.associationValue(item1), 0.001);\r\n        assertEquals(1.0, association.associationValue(item2), 0.001);\r\n        assertEquals(0.0, association.associationValue(item3), 0.001);\r\n        assertEquals(1.0, association.associationValue(item4), 0.001);\r\n        \r\n        assertEquals(3, recommendedItems.size());\r\n        assertEquals(item1, recommendedItems.get(0).getItem());\r\n        assertEquals(2.0, recommendedItems.get(0).getValue().doubleValue(), 0.001);\r\n        assertEquals(1.0, recommendedItems.get(1).getValue().doubleValue(), 0.001);\r\n        assertEquals(1.0, recommendedItems.get(2).getValue().doubleValue(), 0.001);\r\n        \r\n        association = associations.get(item4);\r\n        assertEquals(0.0, association.associationValue(item1), 0.001);\r\n        assertEquals(1.0, association.associationValue(item2), 0.001);\r\n        assertEquals(1.0, association.associationValue(item3), 0.001);\r\n        assertEquals(0.0, association.associationValue(item4), 0.001);\r\n        \r\n    }\r\n\r\n}\r\n',4447,269),(270,'<?xml version=\'1.0\' encoding=\'utf-8\'?>\r\n<!DOCTYPE hibernate-configuration PUBLIC\r\n        \"-//Hibernate/Hibernate Configuration DTD//EN\"\r\n        \"http://www.hibernate.org/dtd/hibernate-configuration-3.0.dtd\">\r\n<hibernate-configuration>\r\n    <session-factory>\r\n    \r\n    	<!-- connection pool configs -->\r\n	    <property name=\"c3p0.acquire_increment\">1</property> \r\n		<property name=\"c3p0.idle_test_period\">100</property> <!-- seconds --> \r\n		<property name=\"c3p0.max_size\">15</property> \r\n		<property name=\"c3p0.min_size\">1</property> \r\n		<property name=\"c3p0.timeout\">600</property>\r\n    \r\n        <property name=\"connection.url\">jdbc:mysql://localhost/commendans_test</property>\r\n        <property name=\"connection.driver_class\">com.mysql.jdbc.Driver</property>\r\n        <property name=\"dialect\">org.hibernate.dialect.MySQL5InnoDBDialect</property>\r\n        <property name=\"connection.username\">root</property>\r\n        <property name=\"connection.password\"></property>\r\n        <property name=\"hibernate.hbm2ddl.auto\">create-drop</property>\r\n        <property name=\"show_sql\">true</property>\r\n        <property name=\"format_sql\">false</property>\r\n        <property name=\"hibernate.generate_statistics\">true</property>\r\n\r\n        <mapping class=\"br.ime.usp.commendans.model.Item\" />\r\n        <mapping class=\"br.ime.usp.commendans.model.Customer\" />\r\n        <mapping class=\"br.ime.usp.commendans.model.ClientApp\" />\r\n\r\n    </session-factory>\r\n</hibernate-configuration>\r\n',1473,270),(271,'package br.ime.usp.commendans.controller;\r\n\r\nimport java.util.ArrayList;\r\nimport java.util.List;\r\n\r\nimport br.com.caelum.vraptor.Get;\r\nimport br.com.caelum.vraptor.Post;\r\nimport br.com.caelum.vraptor.Resource;\r\nimport br.com.caelum.vraptor.Result;\r\nimport br.com.caelum.vraptor.view.Results;\r\nimport br.ime.usp.commendans.dao.ClientAppDao;\r\nimport br.ime.usp.commendans.dao.CustomerDao;\r\nimport br.ime.usp.commendans.dao.ItemDao;\r\nimport br.ime.usp.commendans.model.ClientApp;\r\nimport br.ime.usp.commendans.model.Customer;\r\nimport br.ime.usp.commendans.model.GroupedItems;\r\nimport br.ime.usp.commendans.model.Item;\r\n\r\n@Resource\r\npublic class ClientAppController {\r\n\r\n    private final Result result;\r\n    private final ClientAppDao appDao;\r\n    private final ItemDao itemDao;\r\n    private final CustomerDao customerDao;\r\n\r\n    public ClientAppController(Result result, ClientAppDao appDao, ItemDao itemDao, CustomerDao customerDao) {\r\n        this.result = result;\r\n        this.appDao = appDao;\r\n        this.itemDao = itemDao;\r\n        this.customerDao = customerDao;\r\n    }\r\n    \r\n    //clientAppUserId=100&itemsIds[0]=1&itemsIds[1]=2&itemsIds[2]=3\r\n    @Post(\"app/{key}/addSale\")\r\n    public void addSale(String key, Long clientAppUserId, List<Long> itemsIds) {\r\n        ClientApp app = appDao.findByAccessKey(key);\r\n        GroupedItems groupedItems = itemDao.findItems(itemsIds, app);\r\n        \r\n        ArrayList<Item> newItems = groupedItems.addRemaining(itemsIds, app);\r\n        \r\n        for (Item item : newItems) {\r\n            itemDao.save(item);\r\n        }\r\n        \r\n        Customer customer = customerDao.find(app, clientAppUserId);\r\n        List<Item> allItems = groupedItems.getItems();\r\n        if (customer == null) {\r\n            customer = new Customer(new ArrayList<Item>(), clientAppUserId, app);\r\n        }\r\n        for (Item item : allItems) {\r\n            customer.add(item);\r\n        }\r\n        customerDao.save(customer);\r\n        result.use(Results.json()).from(true).serialize();\r\n    }\r\n    \r\n    @Get(\"app/new\")\r\n    public void clientAppForm() {\r\n    }\r\n    \r\n    @Post(\"app/new\")\r\n    public void saveApp(String name) {\r\n        String accessKey = \"123\" + name;\r\n        appDao.save(new ClientApp(name, accessKey));\r\n        result.include(\"key\", accessKey);\r\n    }\r\n\r\n}\r\n',2308,271),(272,'package br.ime.usp.commendans.controller;\r\n\r\nimport java.util.ArrayList;\r\nimport java.util.List;\r\n\r\nimport br.com.caelum.vraptor.Get;\r\nimport br.com.caelum.vraptor.Resource;\r\nimport br.com.caelum.vraptor.Result;\r\nimport br.com.caelum.vraptor.view.Results;\r\nimport br.ime.usp.commendans.dao.ClientAppDao;\r\nimport br.ime.usp.commendans.dao.ItemDao;\r\nimport br.ime.usp.commendans.infra.TupleJsonSerializer;\r\nimport br.ime.usp.commendans.model.ClientApp;\r\nimport br.ime.usp.commendans.model.Item;\r\nimport br.ime.usp.commendans.recommender.GeneralRecommender;\r\nimport br.ime.usp.commendans.recommender.RecommenderCreator;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.ItemVector;\r\nimport br.ime.usp.commendans.recommender.itemtoitem.Tuple;\r\n\r\n@Resource\r\npublic class ItemToItemController {\r\n    private final ItemDao itemDao;\r\n    private final Result result;\r\n    private final TupleJsonSerializer serializer;\r\n    private final GeneralRecommender recommender;\r\n    private final ClientAppDao appDao;\r\n    private final RecommenderCreator recommenderCreator;\r\n\r\n    public ItemToItemController(GeneralRecommender recommender, \r\n            ItemDao itemDao, ClientAppDao appDao, Result result, \r\n            TupleJsonSerializer serializer, RecommenderCreator recommenderCreator) {\r\n        this.recommender = recommender;\r\n        this.itemDao = itemDao;\r\n        this.appDao = appDao;\r\n        this.result = result;\r\n        this.serializer = serializer;\r\n        this.recommenderCreator = recommenderCreator;\r\n    }\r\n    \r\n    @Get(\"/recommend/item/{appItemId}\")\r\n    public void recommend(Long appItemId, String accessKey) {\r\n        if (validKey(accessKey)) {\r\n            ClientApp app = appDao.findByAccessKey(accessKey);\r\n            Item item = itemDao.findByAppItemId(appItemId, app);\r\n            ItemVector recommended = recommender.recommendedItemsFor(item, accessKey);\r\n            serializeResult(recommended);\r\n        }\r\n    }\r\n    \r\n\r\n    @Get(\"/recommend/items/\")\r\n    public void recommend(List<Long> itemsIds, String accessKey) {\r\n        if (validKey(accessKey)) {\r\n            ClientApp app = appDao.findByAccessKey(accessKey);\r\n            List<Item> items = itemDao.findItems(itemsIds, app).getItems();\r\n            ItemVector recommended = recommender.recommendedItemsFor(items, accessKey);\r\n            serializeResult(recommended);\r\n        }\r\n    }\r\n    \r\n    @Get(\"/recalculate/\")\r\n    public void recalculate() {\r\n        recommenderCreator.create();\r\n        result.use(Results.http()).body(\"ok\");\r\n    }\r\n\r\n    private boolean validKey(String accessKey) {\r\n        ClientApp app = appDao.findByAccessKey(accessKey);\r\n        if (app == null) {\r\n            result.notFound();\r\n            return false;\r\n        }\r\n        return true;\r\n    }\r\n\r\n    private void serializeResult(ItemVector recommended) {\r\n        List<Tuple> tuples = recommended == null ? new ArrayList<Tuple>() : recommended.getTuples();\r\n        result.use(Results.http())\r\n            .addHeader(\"content-type\", \"application/json\")\r\n            .body(serializer.toJson(tuples));\r\n    }\r\n}\r\n',3095,272),(273,'package br.ime.usp.commendans.dao;\r\n\r\nimport java.util.List;\r\n\r\nimport org.hibernate.Query;\r\nimport org.hibernate.Session;\r\n\r\nimport br.com.caelum.vraptor.ioc.Component;\r\nimport br.ime.usp.commendans.model.ClientApp;\r\n\r\n@Component\r\npublic class ClientAppDao {\r\n    \r\n    private final Session session;\r\n\r\n    public ClientAppDao(Session session) {\r\n        this.session = session;\r\n    }\r\n    \r\n    @SuppressWarnings(\"unchecked\")\r\n    public List<ClientApp> list() {\r\n        return session.createCriteria(ClientApp.class).list();\r\n    }\r\n\r\n    public ClientApp findByAccessKey(String key) {\r\n        Query query = session.createQuery(\"select app from ClientApp app where app.accessKey=:key\");\r\n        query.setParameter(\"key\", key);\r\n        return (ClientApp) query.uniqueResult();\r\n    }\r\n\r\n    public void save(ClientApp clientApp) {\r\n        session.save(clientApp);\r\n    }\r\n\r\n    public void commit() {\r\n        session.getTransaction().commit();\r\n    }\r\n\r\n    public void beginTransaction() {\r\n        session.beginTransaction();\r\n    }\r\n\r\n\r\n}\r\n',1053,273),(274,'package br.ime.usp.commendans.dao;\r\n\r\nimport java.util.List;\r\n\r\nimport org.hibernate.Query;\r\nimport org.hibernate.Session;\r\n\r\nimport br.com.caelum.vraptor.ioc.Component;\r\nimport br.com.caelum.vraptor.ioc.PrototypeScoped;\r\nimport br.ime.usp.commendans.model.ClientApp;\r\nimport br.ime.usp.commendans.model.Customer;\r\n\r\n@Component @PrototypeScoped\r\npublic class CustomerDao {\r\n    private final Session session;\r\n\r\n    public CustomerDao(Session session) {\r\n        this.session = session;\r\n    }\r\n    \r\n    @SuppressWarnings(\"unchecked\")\r\n    public List<Customer> list() {\r\n        return session.createCriteria(Customer.class).list();\r\n    }\r\n\r\n    @SuppressWarnings(\"unchecked\")\r\n    public List<Customer> findCustomersOf(ClientApp app) {\r\n        Query query = session.createQuery(\"select customer from Customer customer \" +\r\n        		\"join fetch customer.items \" +\r\n        		\"where customer.app.name like :appName\");\r\n        query.setParameter(\"appName\", app.getName());\r\n        return query.list();\r\n    }\r\n\r\n    public void save(Customer customer) {\r\n        session.save(customer);\r\n    }\r\n\r\n    public Customer find(ClientApp app, Long customerId) {\r\n        Query query = session.createQuery(\"select customer from Customer customer \" +\r\n                \"where customer.app.id like :appId \" +\r\n                \"and customer.clientAppCustomerId = :customerId\");\r\n        query.setParameter(\"appId\", app.getId());\r\n        query.setParameter(\"customerId\", customerId);\r\n        return (Customer) query.uniqueResult();\r\n    }\r\n}\r\n',1538,274),(275,'/***\r\n * Copyright (c) 2009 Caelum - www.caelum.com.br/opensource\r\n * All rights reserved.\r\n *\r\n * Licensed under the Apache License, Version 2.0 (the \"License\");\r\n * you may not use this file except in compliance with the License.\r\n * You may obtain a copy of the License at\r\n *\r\n *  http://www.apache.org/licenses/LICENSE-2.0\r\n *\r\n * Unless required by applicable law or agreed to in writing, software\r\n * distributed under the License is distributed on an \"AS IS\" BASIS,\r\n * WITHOUT WARRANTIES OR CONDITIONS OF ANY KIND, either express or implied.\r\n * See the License for the specific language governing permissions and\r\n * limitations under the License.\r\n */\r\npackage br.ime.usp.commendans.infra;\r\n\r\nimport org.hibernate.Session;\r\nimport org.hibernate.Transaction;\r\n\r\nimport br.com.caelum.vraptor.Intercepts;\r\nimport br.com.caelum.vraptor.Validator;\r\nimport br.com.caelum.vraptor.core.InterceptorStack;\r\nimport br.com.caelum.vraptor.interceptor.Interceptor;\r\nimport br.com.caelum.vraptor.resource.ResourceMethod;\r\n\r\n/**\r\n * An interceptor that manages Hibernate Transaction. All requests are intercepted\r\n * and a transaction is created before execution. If the request has no erros, the transaction\r\n * will commited, or a rollback occurs otherwise.\r\n * \r\n * @author Lucas Cavalcanti\r\n */\r\n@Intercepts\r\npublic class HibernateTransactionInterceptor implements Interceptor {\r\n\r\n    private final Session session;\r\n    private final Validator validator;\r\n\r\n    public HibernateTransactionInterceptor(Session session, Validator validator) {\r\n        this.session = session;\r\n        this.validator = validator;\r\n    }\r\n\r\n    //TODO I think that transaction null check is unnecessary, since we never get null transation (garcia-jj)\r\n    public void intercept(InterceptorStack stack, ResourceMethod method, Object instance) {\r\n        Transaction transaction = null;\r\n        try {\r\n            transaction = session.beginTransaction();\r\n            stack.next(method, instance);\r\n            if (!validator.hasErrors() && transaction != null) {\r\n                transaction.commit();\r\n            }\r\n        } finally {\r\n            if (transaction != null && transaction.isActive()) {\r\n                transaction.rollback();\r\n            }\r\n        }\r\n    }\r\n\r\n    public boolean accepts(ResourceMethod method) {\r\n        return true; // Will intercept all requests\r\n    }\r\n}\r\n',2379,275),(276,'<%@ page language=\"java\" contentType=\"text/html; charset=UTF-8\"\r\n    pageEncoding=\"UTF-8\"%>\r\n<!DOCTYPE html PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\" \"http://www.w3.org/TR/html4/loose.dtd\">\r\n<html>\r\n<head>\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\">\r\n<title>Insert title here</title>\r\n</head>\r\n<body>\r\n\r\n	<form method=\"post\">\r\n		App name: <input type=\"text\" name=\"name\" /> <br>\r\n		<input type=\"submit\"/> \r\n	</form>\r\n\r\n</body>\r\n</html>\r\n',467,276),(277,'<%@ page language=\"java\" contentType=\"text/html; charset=UTF-8\"\r\n    pageEncoding=\"UTF-8\"%>\r\n<!DOCTYPE html PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\" \"http://www.w3.org/TR/html4/loose.dtd\">\r\n<html>\r\n<head>\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\">\r\n<title>Insert title here</title>\r\n</head>\r\n<body>\r\n\r\n<p>\r\n	App saved!\r\n</p>\r\n<p>\r\n	Store your access key: ${key}\r\n</p>\r\n\r\n</body>\r\n</html>\r\n',422,277),(278,'package br.ime.usp.commendans.dao;\r\n\r\nimport java.util.List;\r\n\r\nimport org.hibernate.Query;\r\nimport org.hibernate.Session;\r\n\r\nimport br.com.caelum.vraptor.ioc.Component;\r\nimport br.com.caelum.vraptor.ioc.PrototypeScoped;\r\nimport br.ime.usp.commendans.model.ClientApp;\r\nimport br.ime.usp.commendans.model.Customer;\r\n\r\n@Component @PrototypeScoped\r\npublic class CustomerDao {\r\n    private final Session session;\r\n\r\n    public CustomerDao(Session session) {\r\n        this.session = session;\r\n    }\r\n    \r\n    @SuppressWarnings(\"unchecked\")\r\n    public List<Customer> list() {\r\n        return session.createCriteria(Customer.class).list();\r\n    }\r\n\r\n    @SuppressWarnings(\"unchecked\")\r\n    public List<Customer> findCustomersOf(ClientApp app) {\r\n        Query query = session.createQuery(\"select customer from Customer customer \" +\r\n        		\"where customer.app.name like :appName\");\r\n        query.setParameter(\"appName\", app.getName());\r\n        return query.list();\r\n    }\r\n\r\n    public void save(Customer customer) {\r\n        session.save(customer);\r\n    }\r\n\r\n    public Customer find(ClientApp app, Long customerId) {\r\n        Query query = session.createQuery(\"select customer from Customer customer \" +\r\n                \"where customer.app.id like :appId \" +\r\n                \"and customer.clientAppCustomerId = :customerId\");\r\n        query.setParameter(\"appId\", app.getId());\r\n        query.setParameter(\"customerId\", customerId);\r\n        return (Customer) query.uniqueResult();\r\n    }\r\n}\r\n',1496,278),(279,'package br.ime.usp.commendans.model;\r\n\r\nimport java.util.List;\r\n\r\nimport javax.persistence.Entity;\r\nimport javax.persistence.FetchType;\r\nimport javax.persistence.GeneratedValue;\r\nimport javax.persistence.Id;\r\nimport javax.persistence.ManyToMany;\r\nimport javax.persistence.ManyToOne;\r\n\r\n@Entity\r\npublic class Customer {\r\n    \r\n    @Id @GeneratedValue\r\n    private Long id;\r\n    \r\n    private Long clientAppCustomerId;\r\n\r\n    @ManyToMany(fetch=FetchType.EAGER)\r\n    private List<Item> items;\r\n    \r\n    @ManyToOne\r\n    private ClientApp app;\r\n    \r\n    @Deprecated\r\n    public Customer() {\r\n    }\r\n\r\n    public Customer(List<Item> items, Long clientAppCustomerId, ClientApp app) {\r\n        this.items = items;\r\n        this.clientAppCustomerId = clientAppCustomerId;\r\n        this.app = app;\r\n    }\r\n\r\n    public List<Item> itemsBought() {\r\n        return items;\r\n    }\r\n    \r\n    @Override\r\n    public String toString() {\r\n        return \"User \" + clientAppCustomerId + \" from \" + app;\r\n    }\r\n\r\n    public void add(Item item) {\r\n        items.add(item);\r\n    }\r\n    \r\n    public ClientApp getApp() {\r\n        return app;\r\n    }\r\n    \r\n    public Long getClientAppCustomerId() {\r\n        return clientAppCustomerId;\r\n    }\r\n    \r\n}\r\n',1232,279),(280,'<project xmlns=\"http://maven.apache.org/POM/4.0.0\" xmlns:xsi=\"http://www.w3.org/2001/XMLSchema-instance\"\r\n	xsi:schemaLocation=\"http://maven.apache.org/POM/4.0.0 http://maven.apache.org/maven-v4_0_0.xsd\">\r\n	<modelVersion>4.0.0</modelVersion>\r\n	<groupId>heroku-blank</groupId>\r\n	<artifactId>heroku-blank</artifactId>\r\n	<version>0.0.1-SNAPSHOT</version>\r\n	<name>heroku-blank</name>\r\n\r\n	<properties>\r\n		<project.build.sourceEncoding>UTF-8</project.build.sourceEncoding>\r\n	</properties>\r\n\r\n	<build>\r\n		<outputDirectory>${basedir}/src/main/webapp/WEB-INF/classes</outputDirectory>\r\n		<plugins>\r\n			<plugin>\r\n				<artifactId>maven-compiler-plugin</artifactId>\r\n				<version>2.3.2</version>\r\n				<configuration>\r\n					<source>1.6</source>\r\n					<target>1.6</target>\r\n					<encoding>UTF-8</encoding>\r\n				</configuration>\r\n			</plugin>\r\n            \r\n            <plugin>\r\n                <artifactId>maven-eclipse-plugin</artifactId>\r\n                <version>2.8</version>\r\n                <configuration>\r\n                    <downloadSources>true</downloadSources>\r\n                    <downloadJavadocs>true</downloadJavadocs>\r\n                </configuration>\r\n            </plugin>\r\n            \r\n            <plugin>\r\n				<artifactId>maven-dependency-plugin</artifactId>\r\n				<executions>\r\n					<execution>\r\n						<phase>install</phase>\r\n						<goals>\r\n							<goal>copy-dependencies</goal>\r\n						</goals>\r\n						<configuration>\r\n							<outputDirectory>src/main/webapp/WEB-INF/lib</outputDirectory>\r\n						</configuration>\r\n					</execution>\r\n				</executions>\r\n				</plugin>\r\n            \r\n			<plugin>\r\n				<groupId>org.apache.maven.plugins</groupId>\r\n				<artifactId>maven-war-plugin</artifactId>\r\n				<version>2.1.1</version>\r\n				<configuration>\r\n   					<webXml>src/main/webapp/WEB-INF/web-heroku.xml</webXml>        \r\n  				</configuration>\r\n			</plugin>\r\n			<plugin>\r\n				<groupId>org.mortbay.jetty</groupId>\r\n				<artifactId>maven-jetty-plugin</artifactId>\r\n				<version>6.1.14</version>\r\n				<configuration>\r\n					<scanIntervalSeconds>3</scanIntervalSeconds>\r\n					<stopKey>foo</stopKey>\r\n					<stopPort>9999</stopPort>\r\n					<webAppConfig>\r\n						<contextPath>/</contextPath>\r\n					</webAppConfig>\r\n				</configuration>\r\n			</plugin>\r\n            <plugin>\r\n                <groupId>org.codehaus.mojo</groupId>\r\n      